import React, { useState, useEffect } from 'react';
import { ChevronDown, ChevronUp, Calendar, TrendingUp, DollarSign, Clock, CheckCircle, AlertCircle, Eye } from 'lucide-react';

const PaymentSettlementHub = () => {
  const [selectedDistributors, setSelectedDistributors] = useState(new Set());
  const [expandedDistributors, setExpandedDistributors] = useState(new Set());
  const [expandedBills, setExpandedBills] = useState(new Set());
  const [paymentSummary, setPaymentSummary] = useState({
    totalOutstanding: 0,
    totalSales: 0,
    totalProfit: 0,
    netPayable: 0
  });

  // Mock data - in real app this would come from API
  const [distributors, setDistributors] = useState([
    {
      id: 1,
      name: "Apex Consumer Goods",
      code: "AC",
      category: "Electronics & Appliances",
      paymentStatus: "pending",
      totalOutstanding: 52850,
      todaysSales: 89400,
      todaysProfit: 12340,
      todaysPayable: 77060,
      weeklyPayable: 385300,
      lastPayment: "2025-05-15",
      bills: [
        {
          id: "B001",
          date: "2025-05-20",
          amount: 28450,
          status: "active",
          dueDate: "2025-06-19",
          products: [
            { name: "Samsung TV 55\"", qty: 5, costPrice: 42000, sellingPrice: 45000, soldToday: 2 },
            { name: "iPhone 15", qty: 3, costPrice: 75000, sellingPrice: 79900, soldToday: 1 },
            { name: "AirPods Pro", qty: 8, costPrice: 22000, sellingPrice: 24900, soldToday: 3 }
          ]
        },
        {
          id: "B002", 
          date: "2025-05-25",
          amount: 24400,
          status: "active",
          dueDate: "2025-06-24",
          products: [
            { name: "MacBook Air", qty: 2, costPrice: 95000, sellingPrice: 99900, soldToday: 0 },
            { name: "iPad Pro", qty: 4, costPrice: 85000, sellingPrice: 89900, soldToday: 1 }
          ]
        },
        {
          id: "B003",
          date: "2025-04-15",
          amount: 18600,
          status: "closed",
          paidDate: "2025-05-10",
          products: [
            { name: "Apple Watch", qty: 6, costPrice: 35000, sellingPrice: 37900, soldToday: 0 }
          ]
        }
      ]
    },
    {
      id: 2,
      name: "Metro Supplies",
      code: "MS",
      category: "Fashion & Lifestyle",
      paymentStatus: "pending",
      totalOutstanding: 34680,
      todaysSales: 67600,
      todaysProfit: 14200,
      todaysPayable: 53400,
      weeklyPayable: 267000,
      lastPayment: "2025-05-12",
      bills: [
        {
          id: "B101",
          date: "2025-05-22",
          amount: 19680,
          status: "active", 
          dueDate: "2025-07-06",
          products: [
            { name: "Nike Shoes", qty: 8, costPrice: 7500, sellingPrice: 8500, soldToday: 4 },
            { name: "Levi's Jeans", qty: 12, costPrice: 2800, sellingPrice: 3200, soldToday: 6 }
          ]
        },
        {
          id: "B102",
          date: "2025-05-18",
          amount: 15000,
          status: "active",
          dueDate: "2025-07-02", 
          products: [
            { name: "Adidas T-Shirt", qty: 20, costPrice: 1600, sellingPrice: 1800, soldToday: 8 }
          ]
        }
      ]
    },
    {
      id: 3,
      name: "Bharat Wholesale",
      code: "BW", 
      category: "FMCG & Daily Essentials",
      paymentStatus: "paid",
      totalOutstanding: 0,
      todaysSales: 23400,
      todaysProfit: 3580,
      todaysPayable: 19820,
      weeklyPayable: 99100,
      lastPayment: "2025-05-30",
      bills: [
        {
          id: "B201",
          date: "2025-05-28",
          amount: 12400,
          status: "closed",
          paidDate: "2025-05-30",
          products: [
            { name: "Pantene Shampoo", qty: 30, costPrice: 280, sellingPrice: 320, soldToday: 24 },
            { name: "Maggi Noodles", qty: 60, costPrice: 12, sellingPrice: 14, soldToday: 48 }
          ]
        }
      ]
    }
  ]);

  // Sort distributors - pending payments first
  const sortedDistributors = [...distributors].sort((a, b) => {
    if (a.paymentStatus === 'pending' && b.paymentStatus !== 'pending') return -1;
    if (a.paymentStatus !== 'pending' && b.paymentStatus === 'pending') return 1;
    return b.totalOutstanding - a.totalOutstanding;
  });

  const toggleDistributor = (id) => {
    const newSelected = new Set(selectedDistributors);
    if (newSelected.has(id)) {
      newSelected.delete(id);
    } else {
      newSelected.add(id);
    }
    setSelectedDistributors(newSelected);
  };

  const toggleExpanded = (id) => {
    const newExpanded = new Set(expandedDistributors);
    if (newExpanded.has(id)) {
      newExpanded.delete(id);
    } else {
      newExpanded.add(id);
    }
    setExpandedDistributors(newExpanded);
  };

  const toggleBillExpanded = (billId) => {
    const newExpanded = new Set(expandedBills);
    if (newExpanded.has(billId)) {
      newExpanded.delete(billId);
    } else {
      newExpanded.add(billId);
    }
    setExpandedBills(newExpanded);
  };

  // Calculate payment summary
  useEffect(() => {
    let totalOutstanding = 0;
    let totalSales = 0;
    let totalProfit = 0;
    let netPayable = 0;

    selectedDistributors.forEach(id => {
      const dist = distributors.find(d => d.id === id);
      if (dist) {
        totalOutstanding += dist.totalOutstanding;
        totalSales += dist.todaysSales;
        totalProfit += dist.todaysProfit;
        netPayable += dist.todaysPayable;
      }
    });

    setPaymentSummary({
      totalOutstanding,
      totalSales,
      totalProfit,
      netPayable
    });
  }, [selectedDistributors, distributors]);

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0
    }).format(amount);
  };

  const getBillStatusColor = (status) => {
    return status === 'active' ? 'bg-orange-100 text-orange-800' : 'bg-green-100 text-green-800';
  };

  const getPaymentStatusColor = (status) => {
    return status === 'pending' ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-indigo-600 via-purple-600 to-blue-600 text-white">
        <div className="max-w-7xl mx-auto px-6 py-8">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-4xl font-bold mb-2">Payment Settlement Hub</h1>
              <p className="text-blue-100 text-lg">Manage distributor payments and track sales performance</p>
            </div>
            <div className="bg-white/20 backdrop-blur-lg rounded-2xl p-6 text-center">
              <div className="text-3xl font-bold">{formatCurrency(67600 + 89400 + 23400)}</div>
              <div className="text-blue-100">Today's Total Sales</div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Distributors List */}
          <div className="lg:col-span-2 space-y-6">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-bold text-gray-900">Distributors</h2>
              <button
                onClick={() => setSelectedDistributors(new Set(distributors.map(d => d.id)))}
                className="bg-indigo-600 text-white px-6 py-2 rounded-lg hover:bg-indigo-700 transition-colors font-semibold"
              >
                Select All
              </button>
            </div>

            {sortedDistributors.map((distributor) => (
              <div
                key={distributor.id}
                className={`bg-white rounded-2xl shadow-lg border-2 transition-all duration-300 ${
                  selectedDistributors.has(distributor.id)
                    ? 'border-indigo-500 shadow-indigo-200'
                    : 'border-gray-200 hover:border-gray-300'
                } ${
                  distributor.paymentStatus === 'pending' ? 'ring-2 ring-orange-200' : ''
                }`}
              >
                {/* Distributor Header */}
                <div className="p-6">
                  <div className="flex justify-between items-start mb-4">
                    <div className="flex items-center space-x-4">
                      <div className={`w-16 h-16 rounded-2xl bg-gradient-to-br ${
                        distributor.id === 1 ? 'from-blue-500 to-blue-600' :
                        distributor.id === 2 ? 'from-orange-500 to-orange-600' :
                        'from-green-500 to-green-600'
                      } text-white flex items-center justify-center text-xl font-bold`}>
                        {distributor.code}
                      </div>
                      <div>
                        <h3 className="text-xl font-bold text-gray-900">{distributor.name}</h3>
                        <p className="text-gray-600">{distributor.category}</p>
                        <div className="flex items-center space-x-2 mt-1">
                          <span className={`px-3 py-1 rounded-full text-xs font-semibold ${getPaymentStatusColor(distributor.paymentStatus)}`}>
                            {distributor.paymentStatus === 'pending' ? 'Payment Pending' : 'Paid'}
                          </span>
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center space-x-3">
                      <label className="flex items-center space-x-2 cursor-pointer">
                        <input
                          type="checkbox"
                          checked={selectedDistributors.has(distributor.id)}
                          onChange={() => toggleDistributor(distributor.id)}
                          className="w-5 h-5 text-indigo-600 border-2 border-gray-300 rounded focus:ring-indigo-500"
                        />
                        <span className="font-semibold text-indigo-700">Include in Payment</span>
                      </label>
                    </div>
                  </div>

                  {/* Metrics Grid */}
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
                    <div className="bg-red-50 rounded-xl p-4 border border-red-200">
                      <div className="flex items-center justify-between mb-1">
                        <AlertCircle className="w-5 h-5 text-red-600" />
                        <span className="text-2xl font-bold text-red-700">{formatCurrency(distributor.totalOutstanding)}</span>
                      </div>
                      <p className="text-red-600 text-sm font-medium">Outstanding Bills</p>
                    </div>

                    <div className="bg-blue-50 rounded-xl p-4 border border-blue-200">
                      <div className="flex items-center justify-between mb-1">
                        <TrendingUp className="w-5 h-5 text-blue-600" />
                        <span className="text-2xl font-bold text-blue-700">{formatCurrency(distributor.todaysSales)}</span>
                      </div>
                      <p className="text-blue-600 text-sm font-medium">Today's Sales</p>
                    </div>

                    <div className="bg-green-50 rounded-xl p-4 border border-green-200">
                      <div className="flex items-center justify-between mb-1">
                        <DollarSign className="w-5 h-5 text-green-600" />
                        <span className="text-2xl font-bold text-green-700">{formatCurrency(distributor.todaysProfit)}</span>
                      </div>
                      <p className="text-green-600 text-sm font-medium">Today's Profit</p>
                    </div>

                    <div className="bg-purple-50 rounded-xl p-4 border border-purple-200">
                      <div className="flex items-center justify-between mb-1">
                        <Calendar className="w-5 h-5 text-purple-600" />
                        <span className="text-2xl font-bold text-purple-700">{formatCurrency(distributor.todaysPayable)}</span>
                      </div>
                      <p className="text-purple-600 text-sm font-medium">Today's Payable</p>
                    </div>
                  </div>

                  {/* Bills Section Toggle */}
                  <button
                    onClick={() => toggleExpanded(distributor.id)}
                    className="w-full flex items-center justify-between bg-gray-50 hover:bg-gray-100 rounded-xl p-4 transition-colors"
                  >
                    <div className="flex items-center space-x-2">
                      <Eye className="w-5 h-5 text-gray-600" />
                      <span className="font-semibold text-gray-700">
                        View Bills ({distributor.bills.filter(b => b.status === 'active').length} active, {distributor.bills.filter(b => b.status === 'closed').length} closed)
                      </span>
                    </div>
                    {expandedDistributors.has(distributor.id) ? 
                      <ChevronUp className="w-5 h-5 text-gray-600" /> : 
                      <ChevronDown className="w-5 h-5 text-gray-600" />
                    }
                  </button>
                </div>

                {/* Bills Details */}
                {expandedDistributors.has(distributor.id) && (
                  <div className="border-t border-gray-200 p-6 bg-gray-50">
                    <div className="space-y-4">
                      {/* Active Bills First */}
                      {distributor.bills
                        .filter(bill => bill.status === 'active')
                        .map((bill) => (
                          <div key={bill.id} className="bg-white rounded-xl p-4 border border-orange-200">
                            <div className="flex justify-between items-center mb-3">
                              <div className="flex items-center space-x-3">
                                <span className="font-bold text-gray-900">Bill #{bill.id}</span>
                                <span className={`px-3 py-1 rounded-full text-xs font-semibold ${getBillStatusColor(bill.status)}`}>
                                  {bill.status.toUpperCase()}
                                </span>
                                <span className="text-sm text-gray-600">Due: {bill.dueDate}</span>
                              </div>
                              <div className="text-right">
                                <div className="text-lg font-bold text-gray-900">{formatCurrency(bill.amount)}</div>
                                <button
                                  onClick={() => toggleBillExpanded(bill.id)}
                                  className="text-indigo-600 hover:text-indigo-800 text-sm font-medium"
                                >
                                  {expandedBills.has(bill.id) ? 'Hide Details' : 'View Products'}
                                </button>
                              </div>
                            </div>

                            {expandedBills.has(bill.id) && (
                              <div className="border-t pt-3">
                                <div className="grid grid-cols-6 gap-2 text-xs font-semibold text-gray-600 mb-2">
                                  <div>Product</div>
                                  <div>Qty</div>
                                  <div>Cost Price</div>
                                  <div>Selling Price</div>
                                  <div>Sold Today</div>
                                  <div>Revenue</div>
                                </div>
                                {bill.products.map((product, idx) => (
                                  <div key={idx} className="grid grid-cols-6 gap-2 text-sm py-2 border-b border-gray-100">
                                    <div className="font-medium">{product.name}</div>
                                    <div>{product.qty}</div>
                                    <div>{formatCurrency(product.costPrice)}</div>
                                    <div>{formatCurrency(product.sellingPrice)}</div>
                                    <div className="font-bold text-green-600">{product.soldToday}</div>
                                    <div className="font-bold">{formatCurrency(product.sellingPrice * product.soldToday)}</div>
                                  </div>
                                ))}
                              </div>
                            )}
                          </div>
                        ))}

                      {/* Closed Bills */}
                      {distributor.bills
                        .filter(bill => bill.status === 'closed')
                        .map((bill) => (
                          <div key={bill.id} className="bg-white rounded-xl p-4 border border-green-200 opacity-75">
                            <div className="flex justify-between items-center">
                              <div className="flex items-center space-x-3">
                                <span className="font-bold text-gray-700">Bill #{bill.id}</span>
                                <span className={`px-3 py-1 rounded-full text-xs font-semibold ${getBillStatusColor(bill.status)}`}>
                                  PAID
                                </span>
                                <span className="text-sm text-gray-500">Paid: {bill.paidDate}</span>
                              </div>
                              <div className="text-lg font-bold text-gray-700">{formatCurrency(bill.amount)}</div>
                            </div>
                          </div>
                        ))}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>

          {/* Payment Summary Sidebar */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-2xl shadow-xl p-6 sticky top-6">
              <h3 className="text-xl font-bold text-gray-900 mb-6">Payment Summary</h3>

              <div className="space-y-4 mb-6">
                <div className="flex justify-between items-center py-3 border-b border-gray-200">
                  <span className="text-gray-600">Selected Distributors</span>
                  <span className="font-bold text-gray-900">{selectedDistributors.size}</span>
                </div>

                <div className="flex justify-between items-center py-3 border-b border-gray-200">
                  <span className="text-gray-600">Total Outstanding</span>
                  <span className="font-bold text-red-600">{formatCurrency(paymentSummary.totalOutstanding)}</span>
                </div>

                <div className="flex justify-between items-center py-3 border-b border-gray-200">
                  <span className="text-gray-600">Today's Sales</span>
                  <span className="font-bold text-blue-600">{formatCurrency(paymentSummary.totalSales)}</span>
                </div>

                <div className="flex justify-between items-center py-3 border-b border-gray-200">
                  <span className="text-gray-600">Today's Profit</span>
                  <span className="font-bold text-green-600">{formatCurrency(paymentSummary.totalProfit)}</span>
                </div>

                <div className="flex justify-between items-center py-4 bg-indigo-50 rounded-xl px-4 mt-4">
                  <span className="font-semibold text-indigo-900">Amount to Pay</span>
                  <span className="text-2xl font-bold text-indigo-700">{formatCurrency(paymentSummary.netPayable)}</span>
                </div>
              </div>

              {/* Payment Method */}
              <div className="mb-6">
                <h4 className="font-semibold text-gray-900 mb-3">Payment Method</h4>
                <div className="space-y-2">
                  {['Bank Transfer (NEFT/RTGS)', 'UPI Payment', 'Cheque Payment'].map((method, idx) => (
                    <label key={idx} className="flex items-center space-x-3 p-3 rounded-lg hover:bg-gray-50 cursor-pointer">
                      <input type="radio" name="payment-method" defaultChecked={idx === 0} className="text-indigo-600" />
                      <span className="text-gray-700">{method}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Action Buttons */}
              <div className="space-y-3">
                <button
                  disabled={selectedDistributors.size === 0}
                  className="w-full bg-gradient-to-r from-indigo-600 to-purple-600 text-white py-4 rounded-xl font-bold text-lg hover:from-indigo-700 hover:to-purple-700 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg hover:shadow-xl"
                >
                  Process Payment
                </button>
                <button className="w-full bg-gray-100 text-gray-700 py-3 rounded-xl font-semibold hover:bg-gray-200 transition-colors">
                  Schedule Payment
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentSettlementHub;
