import { useState, useEffect } from 'react';
import React from 'react';
import ProductTable from './table';
import { Search, Filter, RefreshCw, ShoppingCart, Plus, Minus, AlertCircle, Package, ChevronDown, ChevronUp, Check } from 'lucide-react';

export default function FMCGInventoryManagement() {
  // State management for inventory data
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [orderItems, setOrderItems] = useState({});

  // Filter states
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [subcategoryFilter, setSubcategoryFilter] = useState('all');
  const [distributorFilter, setDistributorFilter] = useState('all');
  const [stockFilter, setStockFilter] = useState('all');
  const [expandedProduct, setExpandedProduct] = useState(null);
  const [orderMode, setOrderMode] = useState(false);


  // Categories, subcategories, distributors
  const categories = ['Beverages', 'Snacks', 'Dairy', 'Bakery', 'Personal Care'];
  const subcategories = {
    'Beverages': ['Soft Drinks', 'Juices', 'Water', 'Tea', 'Coffee'],
    'Snacks': ['Chips', 'Cookies', 'Nuts', 'Chocolate', 'Candy'],
    'Dairy': ['Milk', 'Cheese', 'Yogurt', 'Butter', 'Ice Cream'],
    'Bakery': ['Bread', 'Cakes', 'Pastries', 'Cookies', 'Donuts'],
    'Personal Care': ['Soap', 'Shampoo', 'Toothpaste', 'Deodorant', 'Lotion']
  };

  const distributors = ['NationalDistributors', 'RegionalSupplyCo', 'LocalVendors', 'PremiumImports', 'OrganicWholesalers'];

  useEffect(() => {
    // Simulate fetching products from an API
    const fetchProducts = () => {
      // Generate sample inventory data
      const sampleProducts = [];
      let id = 1;

      categories.forEach(category => {
        const relevantSubcategories = subcategories[category];

        relevantSubcategories.forEach(subcategory => {
          // Generate 1-3 products per subcategory
          const numProducts = Math.floor(Math.random() * 3) + 1;

          for (let i = 0; i < numProducts; i++) {
            const productName = `${subcategory} Product ${i+1}`;

            // Generate 1-3 variants per product
            const numVariants = Math.floor(Math.random() * 3) + 1;
            const variants = [];

            for (let j = 0; j < numVariants; j++) {
              const distributor = distributors[Math.floor(Math.random() * distributors.length)];
              const variantValue = ['Regular', 'Large', 'Small', 'Family Size', 'Mini', 
                                    'Chocolate', 'Vanilla', 'Strawberry', 'Orange'][Math.floor(Math.random() * 9)];

              // Generate realistic random data
              const stock = Math.floor(Math.random() * 40);
              const minStockLevel = 10;
              const unitPrice = (Math.random() * 15 + 1.99).toFixed(2);
              const lastOrderDate = new Date(Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000)
                                     .toISOString().split('T')[0];

              variants.push({
                id: id++,
                variantValue,
                distributor,
                stock,
                minStockLevel,
                unitPrice,
                sku: `SKU-${Math.floor(Math.random() * 10000)}`,
                lastOrderDate,
                unit: Math.random() > 0.5 ? 'box' : 'item',
                qtyPerUnit: Math.random() > 0.5 ? Math.floor(Math.random() * 20) + 6 : 1,
                reorderPoint: Math.floor(Math.random() * 5) + 5,
                expiryDate: new Date(Date.now() + Math.random() * 90 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]
              });
            }

            sampleProducts.push({
              id: `p-${id}`,
              name: productName,
              category,
              subcategory,
              image: `/api/placeholder/50/50`,
              variants
            });
          }
        });
      });

      setProducts(sampleProducts);
      setFilteredProducts(sampleProducts);
    };

    fetchProducts();
  }, []);

  // Filter products based on all criteria
  useEffect(() => {
    let result = [...products];

    // Text search
    if (searchTerm) {
      result = result.filter(product => 
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.subcategory.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.variants.some(v => v.distributor.toLowerCase().includes(searchTerm.toLowerCase())) ||
        product.variants.some(v => v.variantValue.toLowerCase().includes(searchTerm.toLowerCase())) ||
        product.variants.some(v => v.sku.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Category filter
    if (categoryFilter !== 'all') {
      result = result.filter(product => product.category === categoryFilter);
    }

    // Subcategory filter
    if (subcategoryFilter !== 'all') {
      result = result.filter(product => product.subcategory === subcategoryFilter);
    }

    // Distributor filter
    if (distributorFilter !== 'all') {
      result = result.filter(product => 
        product.variants.some(variant => variant.distributor === distributorFilter)
      );
    }

    // Stock filter
    if (stockFilter === 'inStock') {
      result = result.filter(product => 
        product.variants.some(variant => variant.stock > 0)
      );
    } else if (stockFilter === 'lowStock') {
      result = result.filter(product => 
        product.variants.some(variant => variant.stock <= variant.minStockLevel && variant.stock > 0)
      );
    } else if (stockFilter === 'outOfStock') {
      result = result.filter(product => 
        product.variants.some(variant => variant.stock === 0)
      );
    } else if (stockFilter === 'needReorder') {
      result = result.filter(product => 
        product.variants.some(variant => variant.stock <= variant.reorderPoint)
      );
    }

    setFilteredProducts(result);
  }, [searchTerm, categoryFilter, subcategoryFilter, distributorFilter, stockFilter, products]);

  // Toggle product expansion to show variants
  const toggleProduct = (productId) => {
    if (expandedProduct === productId) {
      setExpandedProduct(null);
    } else {
      setExpandedProduct(productId);
    }
  };

  // Reset all filters
  const resetFilters = () => {
    setSearchTerm('');
    setCategoryFilter('all');
    setSubcategoryFilter('all');
    setDistributorFilter('all');
    setStockFilter('all');
  };

  // Add/update items in order
  const addToOrder = (variant, quantity) => {
    setOrderItems(prev => ({
      ...prev,
      [variant.id]: {
        variant,
        quantity: (prev[variant.id]?.quantity || 0) + quantity
      }
    }));
  };

  // Remove items from order
  const removeFromOrder = (variantId) => {
    setOrderItems(prev => {
      const newItems = {...prev};
      delete newItems[variantId];
      return newItems;
    });
  };

  // Update order quantity
  const updateOrderQuantity = (variantId, quantity) => {
    if (quantity <= 0) {
      removeFromOrder(variantId);
      return;
    }

    setOrderItems(prev => ({
      ...prev,
      [variantId]: {
        ...prev[variantId],
        quantity
      }
    }));
  };

  // Calculate total order value
  const getTotalOrderValue = () => {
    return Object.values(orderItems).reduce((total, item) => {
      return total + (item.quantity * parseFloat(item.variant.unitPrice));
    }, 0).toFixed(2);
  };

  // Group orders by distributor
  const getOrdersByDistributor = () => {
    const byDistributor = {};

    Object.values(orderItems).forEach(item => {
      const {variant, quantity} = item;
      const {distributor} = variant;

      if (!byDistributor[distributor]) {
        byDistributor[distributor] = [];
      }

      byDistributor[distributor].push({variant, quantity});
    });

    return byDistributor;
  };

  // Count low stock items
  const countLowStockItems = () => {
    let count = 0;
    products.forEach(product => {
      product.variants.forEach(variant => {
        if (variant.stock <= variant.reorderPoint) {
          count++;
        }
      });
    });
    return count;
  };

  // Switch between inventory and order mode
  const toggleOrderMode = () => {
    setOrderMode(!orderMode);
  };

  // Submit order (placeholder function)
  const submitOrder = () => {
    alert(`Order submitted for ${Object.keys(orderItems).length} items from ${Object.keys(getOrdersByDistributor()).length} distributors`);
    setOrderItems({});
    setOrderMode(false);
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 mb-6">
        <h1 className="text-2xl font-bold">FMCG Inventory Management</h1>

        <div className="flex gap-2">
          <button 
            className={`flex items-center gap-1 px-4 py-2 rounded-lg ${orderMode ? 'bg-gray-200' : 'bg-blue-500 text-white'}`}
            onClick={() => setOrderMode(false)}
          >
            <Package className="h-4 w-4" />
            <span>Inventory</span>
          </button>
          <button 
            className={`flex items-center gap-1 px-4 py-2 rounded-lg ${orderMode ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
            onClick={() => setOrderMode(true)}
          >
            <ShoppingCart className="h-4 w-4" />
            <span>Order</span>
            {Object.keys(orderItems).length > 0 && (
              <span className="ml-1 bg-white text-blue-500 text-xs rounded-full w-5 h-5 flex items-center justify-center">
                {Object.keys(orderItems).length}
              </span>
            )}
          </button>
        </div>
      </div>

      {/* Inventory View */}
      {!orderMode && (
        <>
          {/* Search and Filter Bar */}
          <div className="bg-white rounded-lg shadow p-4 mb-4">
            <div className="flex flex-col md:flex-row gap-4 mb-4">
              <div className="relative flex-grow">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center">
                  <Search className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  type="text"
                  placeholder="Search products, SKUs, variants..."
                  className="pl-10 pr-4 py-2 w-full border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              <button
                onClick={resetFilters}
                className="flex items-center gap-2 px-4 py-2 bg-gray-100 rounded-lg hover:bg-gray-200"
              >
                <RefreshCw className="h-4 w-4" />
                <span>Reset</span>
              </button>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
              {/* Category Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
                <select
                  className="w-full border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={categoryFilter}
                  onChange={(e) => {
                    setCategoryFilter(e.target.value);
                    setSubcategoryFilter('all'); // Reset subcategory when category changes
                  }}
                >
                  <option value="all">All Categories</option>
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
              </div>

              {/* Subcategory Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Subcategory</label>
                <select
                  className="w-full border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={subcategoryFilter}
                  onChange={(e) => setSubcategoryFilter(e.target.value)}
                  disabled={categoryFilter === 'all'}
                >
                  <option value="all">All Subcategories</option>
                  {categoryFilter !== 'all' && subcategories[categoryFilter].map(subcategory => (
                    <option key={subcategory} value={subcategory}>{subcategory}</option>
                  ))}
                </select>
              </div>

              {/* Distributor Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Distributor</label>
                <select
                  className="w-full border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={distributorFilter}
                  onChange={(e) => setDistributorFilter(e.target.value)}
                >
                  <option value="all">All Distributors</option>
                  {distributors.map(distributor => (
                    <option key={distributor} value={distributor}>{distributor}</option>
                  ))}
                </select>
              </div>

              {/* Stock Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Stock Status</label>
                <select
                  className="w-full border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  value={stockFilter}
                  onChange={(e) => setStockFilter(e.target.value)}
                >
                  <option value="all">All Stock Status</option>
                  <option value="inStock">In Stock</option>
                  <option value="lowStock">Low Stock</option>
                  <option value="outOfStock">Out of Stock</option>
                  <option value="needReorder">Need Reorder</option>
                </select>
              </div>

              {/* View Action */}
              <div className="flex items-end">
                <button
                  onClick={() => setStockFilter('needReorder')}
                  className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-yellow-100 text-yellow-800 rounded-lg hover:bg-yellow-200"
                >
                  <AlertCircle className="h-4 w-4" />
                  <span>Show Reorder Items ({countLowStockItems()})</span>
                </button>
              </div>
            </div>
          </div>

          {/* Stats Summary */}
          <div className="grid grid-cols-2 sm:grid-cols-4 gap-4 mb-4">
            <div className="bg-white rounded-lg shadow p-3">
              <h3 className="text-sm font-medium text-gray-700">Total Products</h3>
              <p className="text-xl font-bold">{filteredProducts.length}</p>
            </div>
            <div className="bg-white rounded-lg shadow p-3">
              <h3 className="text-sm font-medium text-gray-700">Total Variants</h3>
              <p className="text-xl font-bold">
                {filteredProducts.reduce((total, product) => total + product.variants.length, 0)}
              </p>
            </div>
            <div className="bg-white rounded-lg shadow p-3">
              <h3 className="text-sm font-medium text-gray-700">Out of Stock Items</h3>
              <p className="text-xl font-bold text-red-600">
                {filteredProducts.reduce((total, product) => 
                  total + product.variants.filter(v => v.stock === 0).length, 0)}
              </p>
            </div>
            <div className="bg-white rounded-lg shadow p-3">
              <h3 className="text-sm font-medium text-gray-700">Low Stock Items</h3>
              <p className="text-xl font-bold text-yellow-600">
                {filteredProducts.reduce((total, product) => 
                  total + product.variants.filter(v => v.stock <= v.reorderPoint && v.stock > 0).length, 0)}
              </p>
            </div>
          </div>

          {/* Inventory Table */}
          <div className="bg-white rounded-lg shadow overflow-hidden mb-6">
            {filteredProducts.length > 0 ? (
              <div className="overflow-x-auto">
               <ProductTable /> 
              </div>
            ) : (
              <div className="p-8 text-center">
                <p className="text-xl text-gray-500 mb-4">No products found matching your criteria.</p>
                <button 
                  className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                  onClick={resetFilters}
                >
                  Clear All Filters
                </button>
              </div>
            )}
          </div>
        </>
      )}
   </div>)
}

