  <tr>
    <td className="p-3 sm:p-4">
      <div className="flex items-center space-x-3">
        <div className="w-10 h-10 bg-gradient-to-br from-blue-100 to-blue-200 rounded-lg flex items-center justify-center text-lg flex-shrink-0">
          {product.icon}
        </div>
        <div className="min-w-0">
          <h3 className="font-semibold text-gray-900 text-sm">
            {product.name}
          </h3>
          <p className="text-xs text-gray-500 sm:hidden">{product.distributor}</p>
          <p className="text-xs text-gray-400">{product.variants.length} variants</p>
        </div>
      </div>
    </td>
    <td className="p-3 sm:p-4 text-sm text-gray-600 hidden sm:table-cell">
      {product.distributor}
    </td>
    <td className="p-3 sm:p-4 text-sm font-semibold text-gray-900">
      {getPriceRange(product.variants)}
    </td>
    <td className="p-3 sm:p-4 hidden lg:table-cell">
      <div className="flex items-center">
        <Star className="w-4 h-4 text-yellow-400 fill-current" />
        <span className="text-sm text-gray-600 ml-1">{product.rating}</span>
      </div>
    </td>
    <td className="p-3 sm:p-4 text-right">
      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
        stockInfo.status === 'good' ? 'bg-green-100 text-green-800' :
        stockInfo.status === 'low' ? 'bg-yellow-100 text-yellow-800' :
        'bg-red-100 text-red-800'
      }`}>
        {stockInfo.text}
      </span>
    </td>
  </tr>
)}
</tr>
);
})}
</tbody>
</table>
</div>
</div>
)}
</div>

{/* Cart Sidebar */}
{showCart && (
<div className="fixed inset-y-0 right-0 w-96 bg-white shadow-2xl border-l z-50">
  <div className="h-full flex flex-col">
    <div className="p-4 border-b flex justify-between items-center">
      <h2 className="text-lg font-semibold text-gray-900">Shopping Cart</h2>
      <button
        onClick={() => setShowCart(false)}
        className="p-2 hover:bg-gray-100 rounded-full"
      >
        <X className="w-5 h-5" />
      </button>
    </div>

    <div className="flex-1 overflow-y-auto p-4">
      {cartItems.length === 0 ? (
        <p className="text-gray-500 text-center">Your cart is empty</p>
      ) : (
        <div className="space-y-4">
          {cartItems.map(item => (
            <div key={item.id} className="flex items-start space-x-3 p-3 bg-gray-50 rounded-lg">
              <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center flex-shrink-0">
                {item.productIcon}
              </div>
              <div className="flex-1 min-w-0">
                <h4 className="text-sm font-medium text-gray-900">{item.productName}</h4>
                <p className="text-xs text-gray-500">{item.variantName}</p>
                <div className="mt-1 flex items-center justify-between">
                  <span className="text-xs">
                    {item.quantity} {item.unit}(s) × ₹{item.price.toLocaleString()}
                  </span>
                  <span className="text-sm font-medium">₹{item.totalPrice.toLocaleString()}</span>
                </div>
              </div>
              <button
                onClick={() => removeFromCart(item.id)}
                className="p-1 hover:bg-gray-200 rounded"
              >
                <X className="w-4 h-4" />
              </button>
            </div>
          ))}
        </div>
      )}
    </div>

    {cartItems.length > 0 && (
      <div className="border-t p-4">
        <div className="flex justify-between items-center mb-4">
          <span className="text-gray-600">Total</span>
          <span className="text-lg font-semibold">₹{getCartTotal()}</span>
        </div>
        <button
          onClick={handleCheckout}
          className="w-full py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors font-medium"
        >
          Proceed to Checkout
        </button>
      </div>
    )}
  </div>
</div>
)}
</div>
);
}
