import { Routes, Route, Outlet, NavLink, Navigate } from "react-router-dom";
import { ShoppingCart, Package, CreditCard, User, List } from "lucide-react";
import Shelf from "./Shelf";
import You from "./You";
import Payment from "./Payment";
import Orders from "./Orders";
import Shop from "./Shop";

export default function Layout() {
  const navItems = [
    { name: "Shop", to: "/shop", icon: List },
    { name: "Shelf", to: "/shelf", icon: Package },
    { name: "Payment", to: "/payment", icon: CreditCard },
    { name: "Orders", to: "/orders", icon: ShoppingCart },
    { name: "You", to: "/you", icon: User }
  ];

  return (
    <div className="min-h-screen pb-20"> {/* padding bottom for navbar space */}
      <Routes>
        <Route path="/" element={<Navigate to="/shop" />} />
        <Route path="/shop" element={<Shop />} />
        <Route path="/shelf" element={<Shelf />} />
        <Route path="/payment" element={<Payment />} />
        <Route path="/orders" element={<Orders />} />
        <Route path="/you" element={<You />} />
      </Routes>
      <Outlet />

      {/* Bottom Navigation */}
      <div className="fixed bottom-0 left-0 w-full bg-gray-100 text-black flex justify-around px-4 py-3 shadow-lg border-t">
        {navItems.map(({ name, to, icon: Icon }) => (
          <NavLink
            key={to}
            to={to}
            className={({ isActive }) =>
              `flex flex-col items-center text-sm transition duration-300 ${
                isActive ? "text-blue-600" : "text-gray-600 hover:text-blue-500"
              }`
            }
          >
            <Icon className="w-5 h-5 mb-1" />
            {name}
          </NavLink>
        ))}
      </div>
    </div>
  );
}
