import React, { useState } from "react";

const Orders= () => {
  const [activeTab, setActiveTab] = useState("pending");

  const orders = {
    pending: [
      { id: 1, item: "Product A", quantity: 2 },
      { id: 2, item: "Product B", quantity: 1 },
    ],
    ready: [
      { id: 3, item: "Product C", quantity: 4 },
    ],
    onWay: [
      { id: 4, item: "Product D", quantity: 2 },
    ],
    completed: [
      { id: 5, item: "Product E", quantity: 1 },
    ],
  };

  const TabButton = ({ tabId, label }) => (
    <button
      onClick={() => setActiveTab(tabId)}
      className={`px-4 py-2 font-medium rounded-full transition ${
        activeTab === tabId
          ? "bg-blue-600 text-white"
          : "bg-gray-200 text-gray-800 hover:bg-gray-300"
      }`}
    >
      {label}
    </button>
  );

  const OrderList = ({ data }) => (
    <ul className="space-y-2 mt-4">
      {data.map((order) => (
        <li
          key={order.id}
          className="p-4 bg-white rounded-xl shadow border"
        >
          <p className="font-semibold">{order.item}</p>
          <p className="text-sm text-gray-600">Quantity: {order.quantity}</p>
        </li>
      ))}
    </ul>
  );

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6">Orders Dashboard</h1>

      {/* Navigation Bar */}
      <div className="flex space-x-4 mb-6">
        <TabButton tabId="pending" label="Pending Orders" />
        <TabButton tabId="ready" label="Ready to Order" />
        <TabButton tabId="onWay" label="Orders on Way" />
      </div>

      {/* Content Based on Active Tab */}
      <div>
        {activeTab === "pending" && (
          <>
            <h2 className="text-2xl font-semibold mb-2">ðŸ•’ Pending Orders</h2>
            <OrderList data={orders.pending} />
          </>
        )}

        {activeTab === "ready" && (
          <>
            <h2 className="text-2xl font-semibold mb-2">âœ… Ready to Order</h2>
            <OrderList data={orders.ready} />
          </>
        )}

        {activeTab === "onWay" && (
          <>
            <h2 className="text-2xl font-semibold mb-2">ðŸšš Orders on Way</h2>
            <OrderList data={orders.onWay} />

            <h3 className="text-xl font-semibold mt-6">ðŸ“¦ Completed Orders</h3>
            <OrderList data={orders.completed} />
          </>
        )}
      </div>
    </div>
  );
};

export default Orders;
