import { useState, useEffect } from "react";
import { Search, Filter, Plus, Minus, Mic, ShoppingCart } from "lucide-react";
import API from "../api";
import { FixedSizeList as List } from "react-window";
import AutoSizer from "react-virtualized-auto-sizer";

export default function Shelf() {
  const [hoveredRow, setHoveredRow] = useState(null);
  const [showHiddenColumns, setShowHiddenColumns] = useState(false);
  const [filterType, setFilterType] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [activeCategory, setActiveCategory] = useState("Electronics");
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [orderQuantities, setOrderQuantities] = useState({});
  const [productData, setProductData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const categories = [
    "Electronics",
    "Clothing",
    "Home Goods",
    "Groceries",
    "Beauty",
    "Office Supplies"
  ];

  useEffect(() => {
    fetchProducts();
    // eslint-disable-next-line
  }, []);

  const fetchProducts = async () => {
    try {
      setIsLoading(true);
      setError(null);
      const response = await API.get(`/products`);
      const data = response?.data ?? [];
      setProductData(Array.isArray(data) ? data : []);
      // Initialize order quantities
      const initialQuantities = {};
      (Array.isArray(data) ? data : []).forEach(product => {
        (product.variants || []).forEach(variant => {
          initialQuantities[`${product.id}-${variant.id}`] = { quantity: 0, unit: "box" };
        });
      });
      setOrderQuantities(initialQuantities);
    } catch (error) {
      setError(error.message || "Failed to fetch products");
    } finally {
      setIsLoading(false);
    }
  };

  const highlightText = (text, query) => {
    if (!query) return text;
    const regex = new RegExp(`(${query})`, "gi");
    return (
      <span
        dangerouslySetInnerHTML={{
          __html: text.replace(regex, match => `<mark class="bg-yellow-200">${match}</mark>`)
        }}
      />
    );
  };

  const getSellingPriceRange = (variants) => {
    const prices = variants.map(v => v.sellingPrice);
    const min = Math.min(...prices);
    const max = Math.max(...prices);
    return min === max ? `₹${min}` : `₹${min} - ₹${max}`;
  };
  const getCostPriceRange = (variants) => {
    const prices = variants.map(v => v.costPrice);
    const min = Math.min(...prices);
    const max = Math.max(...prices);
    return min === max ? `₹${min}` : `₹${min} - ₹${max}`;
  };
  const getTotalStock = (variants) => variants.reduce((sum, v) => sum + v.stock, 0);
  const getVariantNames = (variants) => variants.map(v => v.name).join(", ");
  const getExpiryDates = (variants) => [...new Set(variants.map(v => v.expiry))].join(", ");
  const getSKUs = (variants) => variants.map(v => v.sku).join(", ");

  const updateQuantity = (productId, variantId, value) => {
    const key = `${productId}-${variantId}`;
    setOrderQuantities(prev => ({
      ...prev,
      [key]: { ...prev[key], quantity: Math.max(0, value) }
    }));
  };
  const updateUnit = (productId, variantId, unit) => {
    const key = `${productId}-${variantId}`;
    setOrderQuantities(prev => ({
      ...prev,
      [key]: { ...prev[key], unit }
    }));
  };

  const getTotalOrderedItems = (product) => {
    let total = 0;
    product.variants.forEach(variant => {
      const key = `${product.id}-${variant.id}`;
      total += orderQuantities[key]?.quantity || 0;
    });
    return total;
  };

  const addAllToCart = () => {
    const newCartItems = [];
    productData.forEach(product => {
      product.variants.forEach(variant => {
        const key = `${product.id}-${variant.id}`;
        const quantity = orderQuantities[key]?.quantity || 0;
        if (quantity > 0) {
          newCartItems.push({
            id: `${product.id}-${variant.id}`,
            productId: product.id,
            variantId: variant.id,
            productName: product.name,
            productIcon: product.icon,
            variantName: variant.name,
            price: variant.sellingPrice,
            quantity,
            unit: orderQuantities[key]?.unit || "box",
            totalPrice: variant.sellingPrice * quantity,
          });
        }
      });
    });
    if (newCartItems.length === 0) {
      alert("Please select quantity for at least one product variant.");
      return;
    }
    setCartItems(prev => [...prev, ...newCartItems]);
    resetOrderQuantities();
    setShowCart(true);
  };

  const resetOrderQuantities = () => {
    const updatedQuantities = { ...orderQuantities };
    Object.keys(updatedQuantities).forEach(key => {
      updatedQuantities[key] = { ...updatedQuantities[key], quantity: 0 };
    });
    setOrderQuantities(updatedQuantities);
  };

  const getCartTotal = () => cartItems.reduce((sum, item) => sum + item.totalPrice, 0).toFixed(2);

  const startVoiceSearch = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.onresult = (event) => {
      const voiceQuery = event.results[0][0].transcript;
      setSearchQuery(voiceQuery);
      handleSearch(voiceQuery);
    };
    recognition.start();
  };

  const handleSearch = (query) => {
    if (!query) return;
    setSearchQuery(query);
    const matchingProducts = productData.filter(product => matchesSearch(product, query));
    if (matchingProducts.length > 0) {
      const currentCategoryHasMatches = matchingProducts.some(product => product.category === activeCategory);
      if (!currentCategoryHasMatches) {
        setActiveCategory(matchingProducts[0].category);
      }
    }
  };

  const matchesSearch = (product, query) => {
    if (!query) return true;
    const searchTerms = query.toLowerCase().split(" ");
    return searchTerms.every(term => {
      if (product.name.toLowerCase().includes(term)) return true;
      if (product.distributor?.toLowerCase().includes(term)) return true;
      if (product.variants.some(v => v.name.toLowerCase().includes(term))) return true;
      if (product.variants.some(v => v.sku?.toLowerCase().includes(term))) return true;
      return false;
    });
  };

  const hasLowStock = (product) => product.variants.some(v => v.stock > 0 && v.stock <= 5);
  const hasOutOfStock = (product) => product.variants.some(v => v.stock === 0);

  const filteredProducts = productData
    .filter(product => product.category === activeCategory)
    .filter(product => matchesSearch(product, searchQuery))
    .filter(product => {
      if (filterType === "low-stock") return product.variants.some(v => v.stock > 0 && v.stock <= 5);
      if (filterType === "out-of-stock") return product.variants.some(v => v.stock === 0);
      return true;
    });

  // Virtualized row renderer for products
  const Row = ({ index, style }) => {
    const product = filteredProducts[index];
    if (!product) return null;
    return (
      <div style={style} className="mb-4">
        <div
          className="bg-white rounded-xl shadow-md p-4 flex flex-col md:flex-row items-start md:items-center gap-4 hover:shadow-xl transition-all duration-200 border border-gray-100"
          onMouseEnter={() => setHoveredRow(product.id)}
          onMouseLeave={() => setHoveredRow(null)}
        >
          <div className="flex-shrink-0 h-14 w-14 flex items-center justify-center bg-blue-100 rounded-xl text-3xl shadow-sm">
            {product.icon}
          </div>
          <div className="flex-1 min-w-0">
            <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-2">
              <div>
                <div className="text-base font-bold text-blue-900">
                  {highlightText(product.name, searchQuery)}
                </div>
                <div className="text-xs text-gray-500">{product.distributor}</div>
                <div className="text-xs text-gray-400">{getVariantNames(product.variants)}</div>
              </div>
              <div className="flex gap-2 flex-wrap">
                <span className={`text-xs font-semibold ${getTotalStock(product.variants) === 0 ? "text-red-500" : getTotalStock(product.variants) <= 5 ? "text-yellow-500" : "text-green-600"}`}>
                  Stock: {getTotalStock(product.variants)}
                </span>
                <span className="text-xs text-gray-700">Price: {getSellingPriceRange(product.variants)}</span>
                {showHiddenColumns && (
                  <>
                    <span className="text-xs text-gray-500">Cost: {getCostPriceRange(product.variants)}</span>
                    <span className="text-xs text-gray-500">Expiry: {getExpiryDates(product.variants)}</span>
                    <span className="text-xs text-gray-500">SKU: {getSKUs(product.variants)}</span>
                  </>
                )}
              </div>
            </div>
            {/* Expanded order form - only visible on hover */}
            {hoveredRow === product.id && (
              <div className="mt-3 bg-blue-50 p-4 rounded-xl border shadow-sm animate-fade-in">
                <div className="space-y-2">
                  {product.variants.map(variant => (
                    <div key={variant.id} className="flex items-center justify-between flex-wrap gap-2">
                      <span className="text-xs font-medium">
                        {highlightText(variant.name, searchQuery)}
                        {variant.stock === 0 && (
                          <span className="ml-1 text-red-500 text-xs font-semibold">(Out of stock)</span>
                        )}
                        {variant.stock > 0 && variant.stock <= 5 && (
                          <span className="ml-1 text-yellow-500 text-xs font-semibold">(Low stock: {variant.stock})</span>
                        )}
                      </span>
                      <div className="flex items-center">
                        <button
                          className="bg-gray-200 p-1 rounded-l-lg hover:bg-gray-300 transition-colors"
                          onClick={() => {
                            const key = `${product.id}-${variant.id}`;
                            const currentQty = orderQuantities[key]?.quantity || 0;
                            updateQuantity(product.id, variant.id, Math.max(0, currentQty - 1));
                          }}
                        >
                          <Minus className="h-4 w-4" />
                        </button>
                        <input
                          type="number"
                          className="w-12 px-1 py-1 border-t border-b text-center text-xs"
                          min="0"
                          value={orderQuantities[`${product.id}-${variant.id}`]?.quantity || 0}
                          onChange={(e) => updateQuantity(product.id, variant.id, parseInt(e.target.value) || 0)}
                        />
                        <button
                          className="bg-gray-200 p-1 rounded-r-lg hover:bg-gray-300 transition-colors"
                          onClick={() => {
                            const key = `${product.id}-${variant.id}`;
                            const currentQty = orderQuantities[key]?.quantity || 0;
                            updateQuantity(product.id, variant.id, currentQty + 1);
                          }}
                        >
                          <Plus className="h-4 w-4" />
                        </button>
                        <select
                          className="ml-2 border rounded-lg px-2 py-1 text-xs bg-white"
                          value={orderQuantities[`${product.id}-${variant.id}`]?.unit || "box"}
                          onChange={(e) => updateUnit(product.id, variant.id, e.target.value)}
                        >
                          <option value="box">Box</option>
                          <option value="piece">Piece</option>
                          <option value="pack">Pack</option>
                        </select>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
          {/* Status badges */}
          <div className="flex flex-col gap-1 items-end min-w-[90px]">
            {getTotalOrderedItems(product) > 0 && (
              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                {getTotalOrderedItems(product)} ordered
              </span>
            )}
            {hasOutOfStock(product) && (
              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-red-100 text-red-800">
                Out of stock
              </span>
            )}
            {!hasOutOfStock(product) && hasLowStock(product) && (
              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                Low stock
              </span>
            )}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen relative font-sans bg-gradient-to-b from-blue-50 via-white to-white overscroll-none">
      {/* Sticky filter/category/search bar */}
      <div className="sticky top-0 z-50 py-3 px-4 bg-gradient-to-b from-blue-900 to-blue-700 shadow-lg">
        <div className="flex flex-wrap items-center gap-4 justify-between">
          <div className="flex gap-2">
            <button
              onClick={() => setFilterType("all")}
              className={`px-4 py-2 rounded-xl font-semibold ${
                filterType === "all"
                  ? "bg-blue-800 text-white shadow-md"
                  : "bg-gray-100 text-gray-700 border border-gray-200 hover:bg-gray-50 shadow-sm"
              } transition-all`}
            >
              All Items
            </button>
            <button
              onClick={() => setFilterType("low-stock")}
              className={`px-4 py-2 rounded-xl font-semibold ${
                filterType === "low-stock"
                  ? "bg-yellow-500 text-white shadow-md"
                  : "bg-gray-100 text-gray-700 border border-gray-200 hover:bg-gray-50 shadow-sm"
              } transition-all`}
            >
              Low Stock
            </button>
            <button
              onClick={() => setFilterType("out-of-stock")}
              className={`px-4 py-2 rounded-xl font-semibold ${
                filterType === "out-of-stock"
                  ? "bg-red-500 text-white shadow-md"
                  : "bg-gray-100 text-gray-700 border border-gray-200 hover:bg-gray-50 shadow-sm"
              } transition-all`}
            >
              Out of Stock
            </button>
            <button
              onClick={() => setShowHiddenColumns(!showHiddenColumns)}
              className="px-4 py-2 rounded-xl bg-gray-100 text-gray-700 border border-gray-200 flex items-center gap-1 hover:bg-gray-50 shadow-sm transition-all"
            >
              <Filter className="h-4 w-4" />
              {showHiddenColumns ? "Hide" : "Show"} SKU/Expiry/Cost Price
            </button>
          </div>
          {/* Category Navigation */}
          <div className="flex gap-2 overflow-x-auto">
            {categories.map(category => (
              <button
                key={category}
                onClick={() => setActiveCategory(category)}
                className={`px-5 py-2.5 whitespace-nowrap rounded-xl font-semibold transition-all ${
                  activeCategory === category
                    ? "bg-white text-blue-900 shadow-md border border-blue-800"
                    : "bg-blue-100 text-blue-800 border border-blue-200 hover:bg-blue-200 shadow-sm"
                }`}
              >
                {category}
              </button>
            ))}
          </div>
          {/* Search Bar */}
          <div className="relative flex items-center">
            <input
              type="text"
              placeholder="Search products..."
              className="w-64 pl-10 pr-12 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyDownCapture={(e) => {
                if (e.key === "Enter") {
                  handleSearch(e.target.value);
                }
              }}
            />
            <Search className="absolute left-3 text-gray-400 h-5 w-5" />
            <button
              className="absolute right-2 bg-blue-600 text-white px-3 py-1 rounded-lg hover:bg-blue-500 transition"
              onClick={startVoiceSearch}
            >
              <Mic className="h-4 w-4" />
            </button>
          </div>
          {/* Cart Button */}
          <button
            className="relative flex items-center gap-2 px-4 py-2 bg-blue-800 text-white rounded-full hover:bg-blue-700 transition shadow-md"
            onClick={() => setShowCart(!showCart)}
          >
            <ShoppingCart className="h-5 w-5" />
            <span>Cart</span>
            {cartItems.length > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs w-6 h-6 flex items-center justify-center rounded-full">
                {cartItems.length}
              </span>
            )}
          </button>
        </div>
      </div>

      {/* Main Content: Virtualized List */}
      <div className="container mx-auto px-4 py-8 mt-8">
        <div className="bg-white rounded-xl shadow-lg overflow-x-auto">
          <AutoSizer disableHeight>
            {({ width }) => (
              <List
                height={600}
                itemCount={filteredProducts.length}
                itemSize={180}
                width={width}
              >
                {Row}
              </List>
            )}
          </AutoSizer>
        </div>
        {/* Floating Add to Cart Button */}
        <div className="fixed bottom-10 right-10 z-50">
          <button
            onClick={addAllToCart}
            className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-8 py-4 rounded-full shadow-xl hover:scale-105 hover:from-blue-800 hover:to-blue-600 transition-all text-lg font-bold"
          >
            Add to Cart
          </button>
        </div>
      </div>
      {/* ...Cart Drawer/Modal can go here... */}
    </div>
  );
}
