import React, { useState, useEffect, useRef } from "react";
import { Trash, Check, FileText, ArrowDown, ArrowUp, PlusCircle, X, Mic, MicOff } from "lucide-react";

// Product database - we'll expand this to match the Python inventory structure
const productDatabase = [
  { 
    id: "p001",
    name: "Product A", 
    brand: "Brand X", 
    category: "Category 1",
    variants: ["Standard", "Premium", "Light"],
    keywords: ["product a", "a", "product"],
    default_unit: "item",
    rate: 100 
  },
  { 
    id: "p002",
    name: "Product B", 
    brand: "Brand Y", 
    category: "Category 2",
    variants: ["Standard", "Premium"],
    keywords: ["product b", "b"],
    default_unit: "packet",
    rate: 150 
  },
  { 
    id: "p003",
    name: "Product C", 
    brand: "Brand Z", 
    category: "Category 1",
    variants: ["Standard", "Special"],
    keywords: ["product c", "c"],
    default_unit: "box",
    rate: 80 
  },
  { 
    id: "p004",
    name: "Product D", 
    brand: "Brand X", 
    category: "Category 3",
    variants: ["Regular", "Premium"],
    keywords: ["product d", "d"],
    default_unit: "bottle",
    rate: 90 
  },
  { 
    id: "p005",
    name: "Product E", 
    brand: "Brand X", 
    category: "Category 2",
    variants: ["Original"],
    keywords: ["product e", "e"],
    default_unit: "item",
    rate: 120 
  },
  { 
    id: "p006",
    name: "Product F", 
    brand: "Brand Y", 
    category: "Category 3",
    variants: ["Standard"],
    keywords: ["product f", "f"],
    default_unit: "packet",
    rate: 70 
  },
  { 
    id: "p007",
    name: "Product G", 
    brand: "Brand Z", 
    category: "Category 1",
    variants: ["Regular", "Light"],
    keywords: ["product g", "g"],
    default_unit: "box",
    rate: 60 
  },
  { 
    id: "p008",
    name: "Product H", 
    brand: "Brand X", 
    category: "Category 2",
    variants: ["Premium"],
    keywords: ["product h", "h"],
    default_unit: "bottle",
    rate: 200 
  },
];

// Sample initial orders
const dummyOrders = [
  {
    customerName: "Person A",
    orderId: "ORD123",
    items: [
      { id: 1, item: "Product A", brand: "Brand X", quantity: 2, rate: 100 },
      { id: 2, item: "Product B", brand: "Brand Y", quantity: 1, rate: 150 },
      { id: 3, item: "Product C", brand: "Brand Z", quantity: 3, rate: 80 },
    ],
  },
  {
    customerName: "Person B",
    orderId: "ORD456",
    items: [
      { id: 5, item: "Product E", brand: "Brand X", quantity: 1, rate: 120 },
      { id: 6, item: "Product F", brand: "Brand Y", quantity: 2, rate: 70 },
    ],
  },
];

// Port the Python OrderParser class to JavaScript
class OrderParser {
  constructor() {
    this.inventory = productDatabase;

    // Prepare keywords for faster lookup
    this.inventoryKeywords = [];
    this.prepareInventoryKeywords();

    // Common units for measurement (ported from Python)
    this.units = {
      // Weight units
      "kg": {type: "weight", standard: "kg"},
      "kgs": {type: "weight", standard: "kg"},
      "kilo": {type: "weight", standard: "kg"},
      "kilos": {type: "weight", standard: "kg"},
      "kilogram": {type: "weight", standard: "kg"},
      "kilograms": {type: "weight", standard: "kg"},
      "g": {type: "weight", standard: "g"},
      "gram": {type: "weight", standard: "g"},
      "grams": {type: "weight", standard: "g"},
      // Volume units
      "l": {type: "volume", standard: "L"},
      "liter": {type: "volume", standard: "L"},
      "litre": {type: "volume", standard: "L"},
      "liters": {type: "volume", standard: "L"},
      "litres": {type: "volume", standard: "L"},
      "ml": {type: "volume", standard: "ml"},
      "milliliter": {type: "volume", standard: "ml"},
      "millilitre": {type: "volume", standard: "ml"},
      // Package units
      "packet": {type: "package", standard: "packet"},
      "packets": {type: "package", standard: "packet"},
      "pack": {type: "package", standard: "packet"},
      "packs": {type: "package", standard: "packet"},
      "box": {type: "package", standard: "box"},
      "boxes": {type: "package", standard: "box"},
      "bottle": {type: "package", standard: "bottle"},
      "bottles": {type: "package", standard: "bottle"},
      "item": {type: "package", standard: "item"},
      "items": {type: "package", standard: "item"},
    };
  }

  prepareInventoryKeywords() {
    // Create keyword mappings similar to Python implementation
    this.inventory.forEach(product => {
      const keywords = new Set([
        ...product.keywords,
        product.name.toLowerCase(),
        product.brand.toLowerCase()
      ]);

      // Add variants
      product.variants.forEach(variant => {
        keywords.add(variant.toLowerCase());
        // Add combined forms like "Brand + Variant" or "Name + Variant"
        keywords.add(`${product.brand.toLowerCase()} ${variant.toLowerCase()}`);
        keywords.add(`${product.name.toLowerCase()} ${variant.toLowerCase()}`);
      });

      keywords.forEach(keyword => {
        if (keyword && keyword.length > 2) { // Ignore very short keywords
          this.inventoryKeywords.push({keyword, productId: product.id});
        }
      });
    });
  }

  // Fuzzy matching equivalent in JavaScript
  fuzzyMatchProduct(itemText, threshold = 0.6) {
    if (!this.inventory || this.inventory.length === 0) {
      return {name: itemText}, 0.3;
    }

    itemText = itemText.toLowerCase().trim();

    // Try direct keyword matches first
    for (const {keyword, productId} of this.inventoryKeywords) {
      if (keyword === itemText) {
        const product = this.inventory.find(p => p.id === productId);
        return [product, 1.0]; // Exact match
      } else if (keyword.includes(itemText) || itemText.includes(keyword)) {
        const product = this.inventory.find(p => p.id === productId);
        return [product, 0.8]; // Partial match
      }
    }

    // Try fuzzy matching if no direct match found
    // Since JavaScript doesn't have a built-in equivalent to get_close_matches,
    // we'll implement a simple similarity check
    let bestMatch = null;
    let highestSimilarity = threshold;

    for (const {keyword, productId} of this.inventoryKeywords) {
      const similarity = this.calculateSimilarity(keyword, itemText);
      if (similarity > highestSimilarity) {
        highestSimilarity = similarity;
        bestMatch = {keyword, productId};
      }
    }

    if (bestMatch) {
      const product = this.inventory.find(p => p.id === bestMatch.productId);
      return [product, highestSimilarity];
    }

    // If no match, return generic item
    return [{name: itemText}, 0.3];
  }

  // Simple similarity calculation based on common characters
  calculateSimilarity(str1, str2) {
    if (!str1 || !str2) return 0;

    const s1 = str1.toLowerCase();
    const s2 = str2.toLowerCase();

    // Use Levenshtein distance as a base for similarity
    const distance = this.levenshteinDistance(s1, s2);
    const maxLength = Math.max(s1.length, s2.length);

    return 1 - (distance / maxLength);
  }

  // Levenshtein distance implementation
  levenshteinDistance(s1, s2) {
    const m = s1.length;
    const n = s2.length;

    // Create the distance matrix
    const dp = Array(m + 1).fill().map(() => Array(n + 1).fill(0));

    for (let i = 0; i <= m; i++) dp[i][0] = i;
    for (let j = 0; j <= n; j++) dp[0][j] = j;

    for (let i = 1; i <= m; i++) {
      for (let j = 1; j <= n; j++) {
        if (s1[i-1] === s2[j-1]) {
          dp[i][j] = dp[i-1][j-1];
        } else {
          dp[i][j] = 1 + Math.min(
            dp[i-1][j],     // deletion
            dp[i][j-1],     // insertion
            dp[i-1][j-1]    // substitution
          );
        }
      }
    }

    return dp[m][n];
  }

  extractUnitAndItem(itemText) {
    const words = itemText.split(/\s+/);
    let unitInfo = null;

    // Look for unit patterns like "kg of rice" or "bottles of water"
    for (let i = 0; i < words.length; i++) {
      const word = words[i].toLowerCase();
      if (this.units[word]) {
        const unitType = this.units[word].type;
        const unitStandard = this.units[word].standard;

        // Handle "of" pattern (e.g., "kg of rice")
        if (i + 1 < words.length && words[i + 1].toLowerCase() === "of") {
          const remainingWords = words.slice(i + 2).join(" ");
          unitInfo = {
            unit: unitStandard,
            unit_type: unitType
          };
          return [remainingWords, unitInfo];
        }

        // Handle unit without "of" (e.g., "2 kg rice")
        if (i > 0 && i + 1 < words.length && !isNaN(words[i - 1])) {
          const remainingWords = [...words.slice(0, i), ...words.slice(i + 1)].join(" ");
          unitInfo = {
            unit: unitStandard,
            unit_type: unitType
          };
          return [remainingWords, unitInfo];
        }
      }
    }

    // Check for "of" patterns without specific unit (e.g., "packets of maggi")
    const ofPattern = /^([\w\s]+)\s+of\s+([\w\s]+)$/i;
    const match = itemText.match(ofPattern);
    if (match) {
      const unitDesc = match[1].trim();
      const itemDesc = match[2].trim();

      // See if the first part is a recognized unit
      const unitWords = unitDesc.split(/\s+/);
      for (const unitWord of unitWords) {
        if (this.units[unitWord.toLowerCase()]) {
          const unitStandard = this.units[unitWord.toLowerCase()].standard;
          const unitType = this.units[unitWord.toLowerCase()].unit_type;
          unitInfo = {
            unit: unitStandard,
            unit_type: unitType
          };
          return [itemDesc, unitInfo];
        }
      }
    }

    // No unit found or special pattern detected
    return [itemText, unitInfo];
  }

  parseOrder(text, matchThreshold = 0.6) {
    // Remove common prefixes like "I want" or "I need"
    const cleanedText = text.toLowerCase().trim()
      .replace(/^(i want|i need|get me|give me|can i have|i would like)\s+/, '');

    // Pattern to match quantity followed by item
    // This handles formats like "2 packets of product a" or "3 product b"
    const pattern = /(\d+(?:\.\d+)?)\s+([\w\s\-\.]+?)(?=\s+\d+(?:\.\d+)?\s+|$)/g;

    let match;
    const matches = [];
    while ((match = pattern.exec(cleanedText)) !== null) {
      matches.push([match[1], match[2].trim()]);
    }

    const orderItems = [];
    const uncertainItems = [];

    for (const [quantityStr, itemText] of matches) {
      // Parse quantity (handle both integer and decimal values)
      let quantity;
      try {
        quantity = quantityStr.includes('.')
          ? parseFloat(quantityStr)
          : parseInt(quantityStr);
      } catch (e) {
        quantity = 0; // Default fallback
      }

      // Extract unit information if present
      const [itemName, unitInfo] = this.extractUnitAndItem(itemText);

      // Match against inventory using fuzzy matching
      const [product, confidence] = this.fuzzyMatchProduct(itemName, matchThreshold);

      // Create the order item
      const orderItem = {
        quantity: quantity,
        item: product.name || itemName,
        confidence: confidence
      };

      // Add product information if available
      if (product.id) {
        orderItem.product_id = product.id;
        orderItem.brand = product.brand || '';
        orderItem.category = product.category || '';
        orderItem.rate = product.rate || 0;

        // Use product default unit if one is specified and no unit was found in text
        if (!unitInfo && product.default_unit) {
          unitInfo = {
            unit: product.default_unit,
            unit_type: "package" // Assume package for default units
          };
        }
      }

      // Add unit information if available
      if (unitInfo) {
        orderItem.unit = unitInfo.unit;
        orderItem.unit_type = unitInfo.unit_type;
      }

      // Flag uncertain items
      if (confidence < matchThreshold) {
        orderItem.uncertain = true;
        uncertainItems.push(orderItem);
      } else {
        orderItem.uncertain = false;
      }

      // Add unique ID for the item
      orderItem.id = Date.now() + orderItems.length;

      orderItems.push(orderItem);
    }

    // Error handling - if no items were found
    if (orderItems.length === 0) {
      return [[], "No items could be parsed from the input"];
    }

    // Error handling - if uncertain items were found
    if (uncertainItems.length > 0) {
      return [orderItems, `Found ${uncertainItems.length} item(s) with low confidence`];
    }

    return [orderItems, null]; // No errors
  }
}

const Shop = () => {
  const [upcomingOrders, setUpcomingOrders] = useState(dummyOrders);
  const [paymentOrders, setPaymentOrders] = useState([]);
  const [expandedCards, setExpandedCards] = useState({});
  const [showReports, setShowReports] = useState(false);
  const [completedOrders, setCompletedOrders] = useState([]);
  const [showOrderModal, setShowOrderModal] = useState(false);
  const [newOrder, setNewOrder] = useState({ customerName: '', orderId: '', items: [] });
  const [sortMode, setSortMode] = useState("none");

  // States for voice input
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [parsedItems, setParsedItems] = useState([]);
  const [parsingError, setParsingError] = useState(null);
  const recognitionRef = useRef(null);
  const orderParserRef = useRef(null);

  // Initialize order parser
  useEffect(() => {
    // Create the order parser once
    if (!orderParserRef.current) {
      orderParserRef.current = new OrderParser();
    }
  }, []);

  // Generate a unique order ID
  const generateOrderId = () => {
    const timestamp = new Date().getTime();
    const randomNum = Math.floor(Math.random() * 1000);
    return `ORD${timestamp.toString().slice(-4)}${randomNum}`;
  };

  // Initialize recognition
  useEffect(() => {
    if (typeof window !== 'undefined' && 'webkitSpeechRecognition' in window) {
      const SpeechRecognition = window.webkitSpeechRecognition || window.SpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = true;
      recognitionRef.current.interimResults = true;

      recognitionRef.current.onresult = (event) => {
        let interimTranscript = '';
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            setTranscript(prev => prev + ' ' + transcript);
          } else {
            interimTranscript += transcript;
          }
        }
      };

      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error', event.error);
        setIsListening(false);
      };
    }

    // Set default order ID when modal opens
    if (showOrderModal) {
      setNewOrder(prev => ({...prev, orderId: generateOrderId()}));
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, [showOrderModal]);

  const startListening = () => {
    setTranscript('');
    if (recognitionRef.current) {
      recognitionRef.current.start();
      setIsListening(true);
    } else {
      alert('Speech recognition is not supported in this browser.');
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  const parseOrderText = () => {
    if (!transcript.trim()) {
      alert("Please enter or speak your order first");
      return;
    }

    // Use our OrderParser instead of the simple regex
    const [items, error] = orderParserRef.current.parseOrder(transcript);

    if (items.length === 0) {
      alert(error || "No products detected in the order. Please try again.");
      setParsingError(error);
    } else {
      setParsedItems(items);
      setParsingError(error); // This could be a warning like "Found items with low confidence"
    }
  };

  const toggleExpand = (orderId) => {
    setExpandedCards((prev) => ({ ...prev, [orderId]: !prev[orderId] }));
  };

  const moveToPayment = (orderId) => {
    const order = upcomingOrders.find((o) => o.orderId === orderId);
    setPaymentOrders((prev) => [...prev, order]);
    setUpcomingOrders((prev) => prev.filter((o) => o.orderId !== orderId));
  };

  const deleteOrderCard = (orderId) => {
    if (window.confirm("Are you sure to delete this order?")) {
      setUpcomingOrders((prev) => prev.filter((o) => o.orderId !== orderId));
    }
  };

  const deleteItem = (orderId, itemId) => {
    setUpcomingOrders((prev) =>
      prev.map((o) =>
        o.orderId === orderId
          ? { ...o, items: o.items.filter((item) => item.id !== itemId) }
          : o
      )
    );
  };

  const handlePayment = (orderId, mode) => {
    const now = new Date();
    const order = paymentOrders.find((o) => o.orderId === orderId);
    const bill = {
      ...order,
      mode,
      date: now.toLocaleDateString(),
      time: now.toLocaleTimeString(),
    };
    console.log("Generated Bill:", bill);
    if (mode === "credit") {
      alert("Credit bill generated. Redirect to borrower's page...");
    } else {
      alert("Paid bill generated.");
    }
    setPaymentOrders((prev) => prev.filter((o) => o.orderId !== orderId));
    setCompletedOrders((prev) => [...prev, bill]);
  };

  const calculateTotal = (items) =>
    items.reduce((acc, item) => acc + item.quantity * (item.rate || 0), 0);

  const calculateRevenue = () => {
    return completedOrders.reduce((total, order) => {
      return total + calculateTotal(order.items);
    }, 0);
  };

  const calculateProfit = () => {
    return calculateRevenue() * 0.2;
  };

  const sortedUpcomingOrders = [...upcomingOrders].sort((a, b) => {
    const totalA = calculateTotal(a.items);
    const totalB = calculateTotal(b.items);
    if (sortMode === "asc") return totalA - totalB;
    if (sortMode === "desc") return totalB - totalA;
    return 0;
  });

  const renderOrderCard = (order, isPayment = false) => (
    <div key={order.orderId} className="min-w-[280px] max-w-xs bg-white rounded-lg shadow-md p-4 mx-2">
      <div className="flex justify-between items-center mb-2">
        <h2 className="text-md font-bold">{order.customerName || "Custom"}</h2>
        <Trash
          className="w-4 h-4 text-red-500 cursor-pointer"
          onClick={() => deleteOrderCard(order.orderId)}
        />
      </div>
      <div>
        {(expandedCards[order.orderId] ? order.items : order.items.slice(0, 3)).map((item) => (
          <div key={item.id} className="flex justify-between items-center text-sm border-b py-1">
            <div>
              <p className="flex-1">
                {item.item} {item.brand && `(${item.brand})`}
                {item.unit && <span className="text-xs text-gray-500 ml-1">[{item.unit}]</span>}
                {item.uncertain && <span className="text-xs text-red-500 ml-1"></span>}
              </p>
              {!isPayment && (
                <input
                  type="number"
                  min="1"
                  value={item.quantity}
                  onChange={(e) => {
                    const quantity = parseInt(e.target.value);
                    setUpcomingOrders((prev) =>
                      prev.map((o) =>
                        o.orderId === order.orderId
                          ? {
                              ...o,
                              items: o.items.map((i) =>
                                i.id === item.id ? { ...i, quantity } : i
                              ),
                            }
                          : o
                      )
                    );
                  }}
                  className="w-12 border rounded px-1 text-xs"
                />
              )}
            </div>
            {!isPayment && (
              <Trash
                className="w-3 h-3 text-red-400 cursor-pointer"
                onClick={() => deleteItem(order.orderId, item.id)}
              />
            )}
          </div>
        ))}
        {order.items.length > 3 && (
          <button
            className="text-xs text-blue-500 mt-1"
            onClick={() => toggleExpand(order.orderId)}
          >
            {expandedCards[order.orderId] ? "View Less" : "View More"}
          </button>
        )}
        <p className="mt-2 text-sm font-semibold">Total: {calculateTotal(order.items)}</p>
        <p className="text-xs text-gray-500">Order ID: {order.orderId}</p>
        {!isPayment ? (
          <Check
            className="w-5 h-5 text-green-600 cursor-pointer mt-2"
            onClick={() => moveToPayment(order.orderId)}
          />
        ) : (
          <div className="flex gap-2 mt-2">
            {["cash", "upi", "credit"].map((mode) => (
              <button
                key={mode}
                onClick={() => handlePayment(order.orderId, mode)}
                className="text-xs px-2 py-1 bg-black text-white rounded-full"
              >
                {mode.toUpperCase()}
              </button>
            ))}
          </div>
        )}
      </div>
    </div>
  );

  const handleOrderInput = (field, value) => {
    setNewOrder((prev) => ({ ...prev, [field]: value }));
  };

  const handleAddNewOrder = () => {
    if (!newOrder.customerName) {
      alert("Please enter customer name.");
      return;
    }

    const items = parsedItems.length > 0 ? parsedItems : [
      { id: Date.now(), item: "Sample Product", brand: "Brand X", quantity: 1, rate: 100 }
    ];

    const orderWithItems = {
      ...newOrder,
      items: items
    };

    setUpcomingOrders((prev) => [...prev, orderWithItems]);
    setShowOrderModal(false);
    setNewOrder({ customerName: '', orderId: '', items: [] });
    setParsedItems([]);
    setTranscript('');
    setParsingError(null);
  };

  return (
    <div className="p-6 space-y-8 bg-white min-h-screen">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold">Shop Dashboard</h1>
        <button
          onClick={() => setShowOrderModal(true)}
          className="flex items-center bg-black text-white px-4 py-2 rounded-full hover:bg-gray-800"
        >
          <PlusCircle className="mr-2 w-4 h-4" /> Create Order
        </button>
      </div>

      {showOrderModal && (
        <div className="fixed inset-0 bg-black bg-opacity-30 backdrop-blur-sm flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md relative">
            <button
              className="absolute top-2 right-2 text-gray-600 hover:text-black"
              onClick={() => setShowOrderModal(false)}
            >
              <X className="w-5 h-5" />
            </button>
            <h2 className="text-lg font-semibold mb-4">Take Order</h2>
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Customer Name"
                className="w-full px-3 py-2 border rounded"
                value={newOrder.customerName}
                onChange={(e) => handleOrderInput("customerName", e.target.value)}
              />
              <div className="flex items-center">
                <input
                  type="text"
                  placeholder="Order ID (Auto-generated)"
                  className="w-full px-3 py-2 border rounded bg-gray-100"
                  value={newOrder.orderId}
                  readOnly
                />
              </div>

              <div className="relative">
                <textarea
                  placeholder="Enter order details or use voice input..."
                  className="w-full px-3 py-2 border rounded h-32"
                  value={transcript}
                  onChange={(e) => setTranscript(e.target.value)}
                ></textarea>

                <div className="flex justify-between mt-2">
                  {isListening ? (
                    <button
                      onClick={stopListening}
                      className="flex items-center bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                    >
                      <MicOff className="mr-1 w-4 h-4" /> Stop Listening
                    </button>
                  ) : (
                    <button
                      onClick={startListening}
                      className="flex items-center bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                    >
                      <Mic className="mr-1 w-4 h-4" /> Start Listening
                    </button>
                  )}

                  <button
                    onClick={parseOrderText}
                    className="bg-gray-200 text-gray-800 px-3 py-1 rounded hover:bg-gray-300"
                  >
                    Parse Order
                  </button>
                </div>
              </div>

              {parsedItems.length > 0 && (
                <div className="border p-3 rounded bg-gray-50">
                  <h3 className="text-sm font-semibold mb-2">Parsed Items:</h3>
                  <ul className="text-sm">
                    {parsedItems.map((item, index) => (
                      <li key={index} className="flex justify-between mb-1">
                        <span>{item.quantity}x {item.item} ({item.brand})</span>
                        <span>{item.rate * item.quantity}</span>
                      </li>
                    ))}
                  </ul>
                  <div className="mt-2 text-right text-sm font-semibold">
                    Total: {calculateTotal(parsedItems)}
                  </div>
                </div>
              )}

              <button
                onClick={handleAddNewOrder}
                className="w-full bg-black text-white py-2 rounded hover:bg-gray-800"
              >
                Add Order
              </button>
            </div>
          </div>
        </div>
      )}

      <section>
        <h2 className="text-xl font-semibold mb-2">Upcoming Orders</h2>
        <div className="flex items-center gap-2 mb-2">
          <button
            onClick={() => setSortMode(sortMode === "asc" ? "desc" : "asc")}
            className="flex items-center bg-gray-200 px-3 py-1 text-sm rounded hover:bg-gray-300"
          >
            Sort by Total {sortMode === "asc" ? <ArrowUp className="w-4 h-4 ml-1" /> : <ArrowDown className="w-4 h-4 ml-1" />}
          </button>
        </div>
        <div className="flex overflow-x-auto pb-2">
          {upcomingOrders.length === 0 ? (
            <p className="text-gray-500">No upcoming orders.</p>
          ) : (
            <div className="flex">
              {sortedUpcomingOrders.map((order) => renderOrderCard(order))}
            </div>
          )}
        </div>
      </section>

      <section>
        <h2 className="text-xl font-semibold mb-2">Payment Section</h2>
        <div className="flex overflow-x-auto pb-2">
          {paymentOrders.length === 0 ? (
            <p className="text-gray-500">No orders in payment section.</p>
          ) : (
            <div className="flex">
              {paymentOrders.map((order) => renderOrderCard(order, true))}
            </div>
          )}
        </div>
      </section>

      <section>
        <button
          onClick={() => setShowReports(!showReports)}
          className="bg-black text-white px-4 py-2 rounded-full hover:bg-gray-800"
        >
          <FileText className="inline-block mr-2" /> Reports
        </button>
        {showReports && (
          <div className="mt-4 border p-4 rounded-xl bg-gray-100">
            <h3 className="text-lg font-semibold mb-2">Business Summary</h3>
            <p>Total Revenue Today: {calculateRevenue()}</p>
            <p>Profit Today: {calculateProfit()}</p>
            <p>Monthly Revenue: {calculateRevenue()}</p>
            <p>Monthly Profit: {calculateProfit()}</p>
            <div className="mt-4">
              <h4 className="font-semibold mb-2">Completed Orders</h4>
              {completedOrders.length === 0 ? (
                <p className="text-gray-500">No completed orders.</p>
              ) : (
                <ul className="text-sm text-gray-700 space-y-2">
                  {completedOrders.map((order) => (
                    <li key={order.orderId}>
                      <strong>{order.customerName || "Custom"}</strong> - #{order.orderId} - 
                      {calculateTotal(order.items)} - {order.mode.toUpperCase()} on {order.date} at {order.time}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        )}
      </section>
    </div>
  );
};

export default Shop;
