import { useState, useEffect, useRef } from "react";
import { 
  Search, 
  Filter, 
  Plus, 
  Minus, 
  Mic, 
  ShoppingCart, 
  Grid3X3, 
  List,
  Package,
  TrendingUp,
  AlertTriangle,
  X,
  Eye,
  ChevronDown,
  Star,
  ChevronRight,
  MicIcon,
  Building2,
  Tag
} from "lucide-react";

export default function EnhancedInventoryShelf() {
  const [hoveredProduct, setHoveredProduct] = useState(null);
  const [filterType, setFilterType] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [activeCategory, setActiveCategory] = useState("Electronics");
  const [activeSubcategory, setActiveSubcategory] = useState("Laptops");
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [orderQuantities, setOrderQuantities] = useState({});
  const [viewMode, setViewMode] = useState("categories"); // categories or distributors
  const [sortBy, setSortBy] = useState("name");
  const [sortOrder, setSortOrder] = useState("asc");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [globalSearch, setGlobalSearch] = useState(false);

  const speechRecognition = useRef(null);

  // Enhanced mock data with subcategories
  const mockProducts = [
    {
      id: 1,
      name: "MacBook Pro 16-inch",
      category: "Electronics",
      subcategory: "Laptops",
      distributor: "Apple Inc.",
      icon: "💻",
      rating: 4.8,
      variants: [
        {
          id: 101,
          name: "M3 Pro 512GB",
          stock: 12,
          sellingPrice: 249900,
          costPrice: 220000,
          sku: "MBP-M3P-512",
          expiry: "N/A"
        },
        {
          id: 102,
          name: "M3 Max 1TB",
          stock: 3,
          sellingPrice: 349900,
          costPrice: 310000,
          sku: "MBP-M3M-1TB",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 2,
      name: "Dell XPS 13",
      category: "Electronics",
      subcategory: "Laptops",
      distributor: "Dell Technologies",
      icon: "💻",
      rating: 4.6,
      variants: [
        {
          id: 201,
          name: "i7 16GB 512GB",
          stock: 8,
          sellingPrice: 149900,
          costPrice: 130000,
          sku: "DELL-XPS13-i7",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 3,
      name: "iPhone 15 Pro",
      category: "Electronics",
      subcategory: "Smartphones",
      distributor: "Apple Inc.",
      icon: "📱",
      rating: 4.9,
      variants: [
        {
          id: 301,
          name: "128GB Natural Titanium",
          stock: 0,
          sellingPrice: 134900,
          costPrice: 115000,
          sku: "IP15P-128-NT",
          expiry: "N/A"
        },
        {
          id: 302,
          name: "256GB Blue Titanium",
          stock: 8,
          sellingPrice: 144900,
          costPrice: 125000,
          sku: "IP15P-256-BT",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 4,
      name: "Samsung Galaxy S24",
      category: "Electronics",
      subcategory: "Smartphones",
      distributor: "Samsung Electronics",
      icon: "📱",
      rating: 4.7,
      variants: [
        {
          id: 401,
          name: "256GB Titanium Black",
          stock: 15,
          sellingPrice: 84999,
          costPrice: 75000,
          sku: "SGS24-256-TB",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 5,
      name: "Premium Cotton T-Shirt",
      category: "Clothing",
      subcategory: "Casual Wear",
      distributor: "Fashion Co.",
      icon: "👕",
      rating: 4.5,
      variants: [
        {
          id: 501,
          name: "Size M - Navy Blue",
          stock: 25,
          sellingPrice: 1299,
          costPrice: 800,
          sku: "TCT-M-NB",
          expiry: "N/A"
        },
        {
          id: 502,
          name: "Size L - White",
          stock: 2,
          sellingPrice: 1299,
          costPrice: 800,
          sku: "TCT-L-WH",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 6,
      name: "Formal Shirt",
      category: "Clothing",
      subcategory: "Formal Wear",
      distributor: "Style House",
      icon: "👔",
      rating: 4.3,
      variants: [
        {
          id: 601,
          name: "Size L - Blue Checks",
          stock: 4,
          sellingPrice: 2499,
          costPrice: 1800,
          sku: "FS-L-BC",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 7,
      name: "Organic Basmati Rice",
      category: "Groceries",
      subcategory: "Grains & Cereals",
      distributor: "Organic Farms Ltd.",
      icon: "🍚",
      rating: 4.6,
      variants: [
        {
          id: 701,
          name: "5KG Premium",
          stock: 15,
          sellingPrice: 899,
          costPrice: 650,
          sku: "OBR-5KG-P",
          expiry: "2025-12-31"
        }
      ]
    },
    {
      id: 8,
      name: "Olive Oil",
      category: "Groceries",
      subcategory: "Cooking Oils",
      distributor: "Healthy Foods Inc.",
      icon: "🫒",
      rating: 4.4,
      variants: [
        {
          id: 801,
          name: "500ml Extra Virgin",
          stock: 8,
          sellingPrice: 599,
          costPrice: 450,
          sku: "OO-500-EV",
          expiry: "2025-08-15"
        }
      ]
    }
  ];

  const [productData, setProductData] = useState(mockProducts);

  const categoriesWithSubs = {
    "Electronics": {
      icon: "📱",
      subcategories: ["Laptops", "Smartphones", "Tablets", "Accessories"]
    },
    "Clothing": {
      icon: "👕",
      subcategories: ["Casual Wear", "Formal Wear", "Sports Wear", "Footwear"]
    },
    "Home Goods": {
      icon: "🏠",
      subcategories: ["Furniture", "Decor", "Kitchen", "Appliances"]
    },
    "Groceries": {
      icon: "🛒",
      subcategories: ["Grains & Cereals", "Cooking Oils", "Snacks", "Beverages"]
    },
    "Beauty": {
      icon: "💄",
      subcategories: ["Skincare", "Makeup", "Hair Care", "Fragrances"]
    },
    "Office Supplies": {
      icon: "📄",
      subcategories: ["Stationery", "Electronics", "Furniture", "Storage"]
    }
  };

  // Initialize speech recognition
  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      speechRecognition.current = new SpeechRecognition();
      speechRecognition.current.continuous = false;
      speechRecognition.current.interimResults = false;
      speechRecognition.current.lang = 'en-US';

      speechRecognition.current.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setSearchQuery(transcript);
        setGlobalSearch(true);
        setIsListening(false);
      };

      speechRecognition.current.onerror = () => {
        setIsListening(false);
      };

      speechRecognition.current.onend = () => {
        setIsListening(false);
      };
    }
  }, []);

  const startVoiceSearch = () => {
    if (speechRecognition.current) {
      setIsListening(true);
      speechRecognition.current.start();
    }
  };

  // Initialize order quantities
  useEffect(() => {
    const initialQuantities = {};
    productData.forEach(product => {
      product.variants.forEach(variant => {
        initialQuantities[`${product.id}-${variant.id}`] = {
          quantity: 0,
          unit: "box"
        };
      });
    });
    setOrderQuantities(initialQuantities);
  }, [productData]);

  const highlightText = (text, query) => {
    if (!query) return text;
    const regex = new RegExp(`(${query})`, "gi");
    return (
      <span
        dangerouslySetInnerHTML={{
          __html: text.replace(regex, (match) => `<mark class="bg-yellow-200 px-1 rounded">${match}</mark>`),
        }}
      />
    );
  };

  const getStockStatus = (variants) => {
    const totalStock = variants.reduce((sum, v) => sum + v.stock, 0);
    const hasOutOfStock = variants.some(v => v.stock === 0);
    const hasLowStock = variants.some(v => v.stock > 0 && v.stock <= 5);

    if (totalStock === 0) return { status: 'out', color: 'red', text: 'Out of Stock' };
    if (hasOutOfStock || hasLowStock) return { status: 'low', color: 'yellow', text: 'Low Stock' };
    return { status: 'good', color: 'green', text: 'In Stock' };
  };

  const getPriceRange = (variants) => {
    const prices = variants.map(v => v.sellingPrice);
    const min = Math.min(...prices);
    const max = Math.max(...prices);
    return min === max ? `₹${min.toLocaleString()}` : `₹${min.toLocaleString()} - ₹${max.toLocaleString()}`;
  };

  const updateQuantity = (productId, variantId, value) => {
    const key = `${productId}-${variantId}`;
    setOrderQuantities(prev => ({
      ...prev,
      [key]: {
        ...prev[key],
        quantity: Math.max(0, value)
      }
    }));
  };

  // Global search function
  const globalSearchProducts = (products, query) => {
    if (!query) return products;

    return products.filter(product => {
      // Search in product name
      if (product.name.toLowerCase().includes(query.toLowerCase())) return true;

      // Search in distributor name
      if (product.distributor.toLowerCase().includes(query.toLowerCase())) return true;

      // Search in variant names
      if (product.variants.some(variant => 
        variant.name.toLowerCase().includes(query.toLowerCase())
      )) return true;

      return false;
    });
  };

  const filteredAndSortedProducts = (() => {
    let filtered = productData;

    // Apply global search first
    if (globalSearch && searchQuery) {
      filtered = globalSearchProducts(filtered, searchQuery);
    } else {
      // Apply category/subcategory filters
      filtered = filtered.filter(product => {
        if (product.category !== activeCategory) return false;
        if (product.subcategory !== activeSubcategory) return false;

        // Apply local search within category
        if (searchQuery && !globalSearch) {
          if (!product.name.toLowerCase().includes(searchQuery.toLowerCase()) && 
              !product.distributor.toLowerCase().includes(searchQuery.toLowerCase()) &&
              !product.variants.some(v => v.name.toLowerCase().includes(searchQuery.toLowerCase()))) {
            return false;
          }
        }

        return true;
      });
    }

    // Apply stock filters
    if (filterType === "low-stock") {
      filtered = filtered.filter(product => product.variants.some(v => v.stock > 0 && v.stock <= 5));
    } else if (filterType === "out-of-stock") {
      filtered = filtered.filter(product => product.variants.some(v => v.stock === 0));
    }

    // Apply sorting
    return filtered.sort((a, b) => {
      let aVal, bVal;
      switch (sortBy) {
        case 'stock':
          aVal = a.variants.reduce((sum, v) => sum + v.stock, 0);
          bVal = b.variants.reduce((sum, v) => sum + v.stock, 0);
          break;
        case 'price':
          aVal = Math.min(...a.variants.map(v => v.sellingPrice));
          bVal = Math.min(...b.variants.map(v => v.sellingPrice));
          break;
        default:
          aVal = a.name.toLowerCase();
          bVal = b.name.toLowerCase();
      }

      if (sortOrder === 'desc') {
        return aVal < bVal ? 1 : -1;
      }
      return aVal > bVal ? 1 : -1;
    });
  })();

  // Group products by distributor for distributor view
  const groupedByDistributor = filteredAndSortedProducts.reduce((acc, product) => {
    if (!acc[product.distributor]) {
      acc[product.distributor] = [];
    }
    acc[product.distributor].push(product);
    return acc;
  }, {});

  const handleCategoryChange = (category) => {
    setActiveCategory(category);
    setActiveSubcategory(categoriesWithSubs[category].subcategories[0]);
    setGlobalSearch(false);
    setSearchQuery("");
  };

  const handleSearchChange = (value) => {
    setSearchQuery(value);
    if (value.trim()) {
      setGlobalSearch(true);
    } else {
      setGlobalSearch(false);
    }
  };

  const ProductTableRow = ({ product, isHovered, onHover, onLeave }) => {
    const stockInfo = getStockStatus(product.variants);

    return (
      <>
        <tr
          className={`border-b border-gray-100 hover:bg-gray-50 transition-colors ${
            isHovered ? 'bg-blue-50' : ''
          }`}
          onMouseEnter={onHover}
          onMouseLeave={onLeave}
        >
          <td className="px-4 py-3">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-br from-blue-100 to-blue-200 rounded-lg flex items-center justify-center text-lg flex-shrink-0">
                {product.icon}
              </div>
              <div className="min-w-0">
                <div className="font-medium text-gray-900 text-sm">
                  {highlightText(product.name, searchQuery)}
                </div>
                <div className="text-xs text-gray-500">{product.distributor}</div>
              </div>
            </div>
          </td>
          <td className="px-4 py-3 text-sm">
            {globalSearch ? `${product.category} > ${product.subcategory}` : product.subcategory}
          </td>
          <td className="px-4 py-3 text-sm font-medium text-gray-900">
            {getPriceRange(product.variants)}
          </td>
          <td className="px-4 py-3 text-sm">
            {product.variants.reduce((sum, v) => sum + v.stock, 0)} units
          </td>
          <td className="px-4 py-3">
            <div className="flex items-center">
              <Star className="w-3 h-3 text-yellow-400 fill-current mr-1" />
              <span className="text-xs text-gray-600">{product.rating}</span>
            </div>
          </td>
          <td className="px-4 py-3">
            <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
              stockInfo.status === 'good' ? 'bg-green-100 text-green-800' :
              stockInfo.status === 'low' ? 'bg-yellow-100 text-yellow-800' :
              'bg-red-100 text-red-800'
            }`}>
              {stockInfo.text}
            </span>
          </td>
        </tr>

        {/* Hover details row */}
        {isHovered && (
          <tr className="bg-blue-50 border-b border-gray-100">
            <td colSpan="6" className="px-4 py-3">
              <div className="space-y-2">
                <div className="text-xs font-medium text-gray-700 mb-2">Variants:</div>
                {product.variants.map(variant => (
                  <div key={variant.id} className="flex items-center justify-between py-1">
                    <div className="flex-1">
                      <span className="text-xs font-medium text-gray-900">{variant.name}</span>
                      <span className="text-xs text-gray-500 ml-2">({variant.sku})</span>
                    </div>
                    <div className="flex items-center space-x-4 text-xs">
                      <span className="text-green-600">SP: ₹{variant.sellingPrice.toLocaleString()}</span>
                      <span className="text-blue-600">CP: ₹{variant.costPrice.toLocaleString()}</span>
                      <span className={`font-medium ${
                        variant.stock === 0 ? 'text-red-500' : 
                        variant.stock <= 5 ? 'text-yellow-500' : 'text-green-500'
                      }`}>
                        {variant.stock} units
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </td>
          </tr>
        )}
      </>
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50">
      {/* Header */}
      <div className="sticky top-0 z-50 bg-white shadow-lg border-b">
        <div className="px-4 lg:px-6 py-4">
          {/* Top Bar */}
          <div className="flex flex-col lg:flex-row lg:items-center justify-between mb-4 space-y-4 lg:space-y-0">
            <div className="flex items-center space-x-4">
              <h1 className="text-xl lg:text-2xl font-bold text-gray-900">Inventory</h1>
              <span className="text-sm text-gray-500">
                {filteredAndSortedProducts.length} products
              </span>
            </div>

            <div className="flex items-center space-x-2 lg:space-x-4">
              {/* View Mode Toggle */}
              <div className="flex items-center bg-gray-100 rounded-lg p-1">
                <button
                  onClick={() => setViewMode('categories')}
                  className={`p-2 rounded-md transition-colors ${
                    viewMode === 'categories' ? 'bg-white shadow-sm text-blue-600' : 'text-gray-500'
                  }`}
                  title="View by Categories"
                >
                  <Tag className="w-4 h-4" />
                </button>
                <button
                  onClick={() => setViewMode('distributors')}
                  className={`p-2 rounded-md transition-colors ${
                    viewMode === 'distributors' ? 'bg-white shadow-sm text-blue-600' : 'text-gray-500'
                  }`}
                  title="View by Distributors"
                >
                  <Building2 className="w-4 h-4" />
                </button>
              </div>

              {/* Sort Dropdown */}
              <div className="relative">
                <select
                  value={`${sortBy}-${sortOrder}`}
                  onChange={(e) => {
                    const [field, order] = e.target.value.split('-');
                    setSortBy(field);
                    setSortOrder(order);
                  }}
                  className="appearance-none bg-white border border-gray-300 rounded-lg px-3 py-2 pr-8 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="name-asc">Name A-Z</option>
                  <option value="name-desc">Name Z-A</option>
                  <option value="price-asc">Price Low-High</option>
                  <option value="price-desc">Price High-Low</option>
                  <option value="stock-desc">Stock High-Low</option>
                  <option value="stock-asc">Stock Low-High</option>
                </select>
                <ChevronDown className="absolute right-2 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400 pointer-events-none" />
              </div>

              {/* Cart Button */}
              <button
                onClick={() => setShowCart(!showCart)}
                className="relative flex items-center space-x-2 bg-blue-600 text-white px-3 lg:px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
              >
                <ShoppingCart className="w-4 lg:w-5 h-4 lg:h-5" />
                <span className="hidden lg:inline">Cart</span>
                {cartItems.length > 0 && (
                  <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs w-5 h-5 flex items-center justify-center rounded-full">
                    {cartItems.length}
                  </span>
                )}
              </button>
            </div>
          </div>

          {/* Navigation and Search */}
          <div className="space-y-4">
            {/* Categories/Distributors Navigation */}
            {!globalSearch && viewMode === 'categories' && (
              <div className="flex flex-col space-y-3">
                {/* Category Tabs */}
                <div className="flex space-x-1 bg-gray-100 rounded-lg p-1 overflow-x-auto">
                  {Object.entries(categoriesWithSubs).map(([category, data]) => (
                    <button
                      key={category}
                      onClick={() => handleCategoryChange(category)}
                      className={`px-3 py-2 rounded-md text-sm font-medium transition-colors whitespace-nowrap flex items-center space-x-2 ${
                        activeCategory === category
                          ? 'bg-white text-blue-600 shadow-sm'
                          : 'text-gray-600 hover:text-gray-900'
                      }`}
                    >
                      <span>{data.icon}</span>
                      <span>{category}</span>
                    </button>
                  ))}
                </div>

                {/* Subcategory Navigation */}
                <div className="flex items-center space-x-2 overflow-x-auto">
                  <span className="text-sm text-gray-500 whitespace-nowrap">Subcategories:</span>
                  <div className="flex space-x-1">
                    {categoriesWithSubs[activeCategory]?.subcategories.map(subcategory => (
                      <button
                        key={subcategory}
                        onClick={() => setActiveSubcategory(subcategory)}
                        className={`px-3 py-1 rounded-md text-sm transition-colors whitespace-nowrap ${
                          activeSubcategory === subcategory
                            ? 'bg-blue-100 text-blue-700'
                            : 'text-gray-600 hover:bg-gray-100'
                        }`}
                      >
                        {subcategory}
                      </button>
                    ))}
                  </div>
                </div>
              </div>
            )}

            {/* Filters and Search */}
            <div className="flex flex-col lg:flex-row lg:items-center gap-4">
              {/* Filter Buttons */}
              <div className="flex space-x-2 overflow-x-auto">
                {[
                  { key: 'all', label: 'All Items', color: 'blue' },
                  { key: 'low-stock', label: 'Low Stock', color: 'yellow' },
                  { key: 'out-of-stock', label: 'Out of Stock', color: 'red' }
                ].map(filter => (
                  <button
                    key={filter.key}
                    onClick={() => setFilterType(filter.key)}
                    className={`px-3 py-2 rounded-lg text-sm font-medium transition-colors whitespace-nowrap ${
                      filterType === filter.key
                        ? `bg-${filter.color}-100 text-${filter.color}-800 border border-${filter.color}-300`
                        : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                    }`}
                  >
                    {filter.label}
                  </button>
                ))}
              </div>

              {/* Search with Voice */}
              <div className="relative flex-1 max-w-md">
                <input
                  type="text"
                  placeholder={globalSearch ? "Search across all products..." : "Search in current category..."}
                  value={searchQuery}
                  onChange={(e) => handleSearchChange(e.target.value)}
                  className="w-full pl-10 pr-20 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />

                {/* Voice Search Button */}
                <button
                  onClick={startVoiceSearch}
                  disabled={!speechRecognition.current}
                  className={`absolute right-2 top-1/2 transform -translate-y-1/2 w-16 h-7 rounded-md flex items-center justify-center transition-colors ${
                    isListening 
                      ? 'bg-red-500 text-white animate-pulse' 
                      : 'bg-blue-500 hover:bg-blue-600 text-white'
                  } ${!speechRecognition.current ? 'opacity-50 cursor-not-allowed' : ''}`}
                  title={isListening ? 'Listening...' : 'Voice Search'}
                >
                  <Mic className="w-4 h-4" />
                  {isListening && (
                    <div className="absolute inset-0 rounded-md border-2 border-red-300 animate-ping"></div>
                  )}
                </button>
              </div>
            </div>

            {/* Global Search Indicator */}
            {globalSearch && searchQuery && (
              <div className="flex items-center justify-between bg-blue-50 border border-blue-200 rounded-lg px-4 py-2">
                <span className="text-sm text-blue-800">
                  Searching across all categories for "{searchQuery}"
                </span>
                <button
                  onClick={() => {
                    setGlobalSearch(false);
                    setSearchQuery("");
                  }}
                  className="text-blue-600 hover:text-blue-800"
                >
                  <X className="w-4 h-4" />
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Main Content: Virtualized List */}
      <div className="container mx-auto px-4 py-8 mt-8">
        <div className="bg-white rounded-xl shadow-lg overflow-x-auto">
          <AutoSizer disableHeight>
            {({ width }) => (
              <List
                height={600}
                itemCount={filteredProducts.length}
                itemSize={180}
                width={width}
              >
                {Row}
              </List>
            )}
          </AutoSizer>
        </div>
        {/* Floating Add to Cart Button */}
        <div className="fixed bottom-10 right-10 z-50">
          <button
            onClick={addAllToCart}
            className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-8 py-4 rounded-full shadow-xl hover:scale-105 hover:from-blue-800 hover:to-blue-600 transition-all text-lg font-bold"
          >
            Add to Cart
          </button>
        </div>
      </div>
      {/* ...Cart Drawer/Modal can go here... */}
    </div>
  );
}
