import { useAuth } from "./AuthContext";
import React, { useState } from "react";
import Navbar from "./Navbar";
import Footer from "./Footer";
import Login from "../pages/Login";
const PrivateRoute = ({ children, triggerLogin }) => {
  const { isAuthenticated } = useAuth();
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);

    const openLoginModal = () => setIsLoginModalOpen(true);
    const closeLoginModal = () => setIsLoginModalOpen(false);

  if (!isAuthenticated) {
     return (
        <div className="flex flex-col min-h-screen">
          {/* Navbar */}
          <Navbar onLoginClick={openLoginModal} />
          {/* Page Content */}
          <main className={`flex-grow ${isLoginModalOpen ? "blur-background" : ""}`}>{<div>{children}</div>}</main>

          {/* Footer */}
          <Footer />
          {openLoginModal()}
          {/* Login Modal */}
          {isLoginModalOpen && (
            <div
              className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 backdrop-blur-sm"
              onClick={closeLoginModal}
            >
              <div
                className="bg-white p-8 rounded-lg shadow-lg w-full max-w-lg"
                onClick={(e) => e.stopPropagation()}
              >
                <Login />
              </div>
            </div>
          )}
        </div>
      ); // Simulate clicking the login button
    return null; // Prevent rendering the protected content
  }

  return children;
};

export default PrivateRoute;
