import { useState, useEffect, useRef } from "react";
import { 
  Search, 
  Filter, 
  Plus, 
  Minus, 
  Mic, 
  ShoppingCart, 
  Grid3X3, 
  List,
  Package,
  TrendingUp,
  AlertTriangle,
  X,
  Eye,
  ChevronDown,
  ChevronRight,
  Star,
  Building2,
  Tag,
  MicOff
} from "lucide-react";

export default function Shelf() {
  const [hoveredProduct, setHoveredProduct] = useState(null);
  const [filterType, setFilterType] = useState("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [activeCategory, setActiveCategory] = useState("Electronics");
  const [activeSubcategory, setActiveSubcategory] = useState("Laptops");
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [orderQuantities, setOrderQuantities] = useState({});
  const [viewMode, setViewMode] = useState("categories"); // categories or distributors
  const [sortBy, setSortBy] = useState("name");
  const [sortOrder, setSortOrder] = useState("asc");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [showGlobalSearch, setShowGlobalSearch] = useState(false);

  const recognitionRef = useRef(null);

  // Enhanced product data with subcategories
  const mockProducts = [
    {
      id: 1,
      name: "MacBook Pro 16-inch",
      category: "Electronics",
      subcategory: "Laptops",
      distributor: "Apple Inc.",
      icon: "ðŸ’»",
      rating: 4.8,
      variants: [
        {
          id: 101,
          name: "M3 Pro 512GB Space Black",
          stock: 12,
          sellingPrice: 249900,
          costPrice: 220000,
          sku: "MBP-M3P-512-SB",
          expiry: "N/A"
        },
        {
          id: 102,
          name: "M3 Max 1TB Silver",
          stock: 3,
          sellingPrice: 349900,
          costPrice: 310000,
          sku: "MBP-M3M-1TB-SL",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 2,
      name: "iPhone 15 Pro",
      category: "Electronics",
      subcategory: "Smartphones",
      distributor: "Apple Inc.",
      icon: "ðŸ“±",
      rating: 4.9,
      variants: [
        {
          id: 201,
          name: "128GB Natural Titanium",
          stock: 0,
          sellingPrice: 134900,
          costPrice: 115000,
          sku: "IP15P-128-NT",
          expiry: "N/A"
        },
        {
          id: 202,
          name: "256GB Blue Titanium",
          stock: 8,
          sellingPrice: 144900,
          costPrice: 125000,
          sku: "IP15P-256-BT",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 3,
      name: "Dell XPS 13",
      category: "Electronics",
      subcategory: "Laptops",
      distributor: "Dell Technologies",
      icon: "ðŸ’»",
      rating: 4.6,
      variants: [
        {
          id: 301,
          name: "Intel i7 16GB 512GB",
          stock: 5,
          sellingPrice: 129900,
          costPrice: 110000,
          sku: "XPS13-I7-16-512",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 4,
      name: "Samsung Galaxy S24",
      category: "Electronics",
      subcategory: "Smartphones",  
      distributor: "Samsung Electronics",
      icon: "ðŸ“±",
      rating: 4.7,
      variants: [
        {
          id: 401,
          name: "256GB Phantom Black",
          stock: 15,
          sellingPrice: 89900,
          costPrice: 75000,
          sku: "S24-256-PB",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 5,
      name: "Premium Cotton T-Shirt",
      category: "Clothing",
      subcategory: "Casual Wear",
      distributor: "Fashion Co.",
      icon: "ðŸ‘•",
      rating: 4.5,
      variants: [
        {
          id: 501,
          name: "Size M Navy Blue",
          stock: 25,
          sellingPrice: 1299,
          costPrice: 800,
          sku: "TCT-M-NB",
          expiry: "N/A"
        },
        {
          id: 502,
          name: "Size L White",
          stock: 2,
          sellingPrice: 1299,
          costPrice: 800,
          sku: "TCT-L-WH",
          expiry: "N/A"
        }
      ]
    },
    {
      id: 6,
      name: "Organic Basmati Rice",
      category: "Groceries",
      subcategory: "Grains & Cereals",
      distributor: "Organic Farms Ltd.",
      icon: "ðŸš",
      rating: 4.6,
      variants: [
        {
          id: 601,
          name: "5KG Premium Quality",
          stock: 15,
          sellingPrice: 899,
          costPrice: 650,
          sku: "OBR-5KG-P",
          expiry: "2025-12-31"
        }
      ]
    }
  ];

  const [productData, setProductData] = useState(mockProducts);


  const categoryStructure = {
    "Electronics": {
      "Laptops": ["MacBook Pro 16-inch", "Dell XPS 13"],
      "Smartphones": ["iPhone 15 Pro", "Samsung Galaxy S24"],
      "Tablets": [],
      "Accessories": []
    },
    "Clothing": {
      "Casual Wear": ["Premium Cotton T-Shirt"],
      "Formal Wear": [],
      "Footwear": [],
      "Accessories": []
    },
    "Home Goods": {
      "Furniture": [],
      "Decor": [],
      "Kitchen": [],
      "Cleaning": []
    },
    "Groceries": {
      "Grains & Cereals": ["Organic Basmati Rice"],
      "Fruits & Vegetables": [],
      "Dairy": [],
      "Beverages": []
    },
    "Beauty": {
      "Skincare": [],
      "Makeup": [],
      "Hair Care": [],
      "Fragrances": []
    },
    "Office Supplies": {
      "Stationery": [],
      "Electronics": [],
      "Furniture": [],
      "Storage": []
    }
  };

  // Initialize order quantities
  useEffect(() => {
    const initialQuantities = {};
    productData.forEach(product => {
      product.variants.forEach(variant => {
        initialQuantities[`${product.id}-${variant.id}`] = {
          quantity: 0,
          unit: "box"
        };
      });
    });
    setOrderQuantities(initialQuantities);
  }, [productData]);



// Add these new functions
const updateUnit = (productId, variantId, unit) => {
  const key = `${productId}-${variantId}`;
  setOrderQuantities(prev => ({
    ...prev,
    [key]: {
      ...prev[key],
      unit
    }
  }));
};

const addToCart = (product) => {
  const newItems = product.variants.map(variant => {
    const key = `${product.id}-${variant.id}`;
    const quantity = orderQuantities[key]?.quantity || 0;
    if (quantity > 0) {
      return {
        id: key,
        productId: product.id,
        variantId: variant.id,
        productName: product.name,
        productIcon: product.icon,
        variantName: variant.name,
        price: variant.sellingPrice,
        quantity,
        unit: orderQuantities[key]?.unit || "box",
        totalPrice: variant.sellingPrice * quantity
      };
    }
    return null;
  }).filter(Boolean);

  if (newItems.length === 0) {
    alert("Please select quantity for at least one variant");
    return;
  }

  setCartItems(prev => [...prev, ...newItems]);

  // Reset quantities for this product's variants
  const resetQuantities = {};
  product.variants.forEach(variant => {
    const key = `${product.id}-${variant.id}`;
    resetQuantities[key] = {
      ...orderQuantities[key],
      quantity: 0
    };
  });

  setOrderQuantities(prev => ({
    ...prev,
    ...resetQuantities
  }));
};

const removeFromCart = (itemId) => {
  setCartItems(prev => prev.filter(item => item.id !== itemId));
};

const getCartTotal = () => {
  return cartItems.reduce((total, item) => total + item.totalPrice, 0).toLocaleString();
};

const handleCheckout = async () => {
  try {
    setIsLoading(true);

    // Prepare order data
    const orderData = {
      items: cartItems.map(item => ({
        productId: item.productId,
        variantId: item.variantId,
        quantity: item.quantity,
        unit: item.unit
      })),
      total: parseFloat(getCartTotal().replace(/,/g, ''))
    };

    // Here you would normally make an API call to your backend
    // const response = await fetch('/api/orders', {
    //   method: 'POST',
    //   headers: { 'Content-Type': 'application/json' },
    //   body: JSON.stringify(orderData)
    // });

    // For now, we'll just simulate a successful order
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Clear cart after successful order
    setCartItems([]);
    setShowCart(false);

    // Show success message
    alert('Order placed successfully!');
  } catch (error) {
    console.error('Checkout error:', error);
    alert('Failed to place order. Please try again.');
  } finally {
    setIsLoading(false);
  }
};

// ...rest of your existing code...
  // Global search function
  const performGlobalSearch = (query) => {
    if (!query.trim()) {
      setSearchResults([]);
      setShowGlobalSearch(false);
      return;
    }

    const results = [];
    const queryLower = query.toLowerCase();

    productData.forEach(product => {
      // Search in product name
      if (product.name.toLowerCase().includes(queryLower)) {
        results.push({ ...product, matchType: 'product' });
      }
      // Search in distributor name
      else if (product.distributor.toLowerCase().includes(queryLower)) {
        results.push({ ...product, matchType: 'distributor' });
      }
      // Search in variant names
      else {
        const matchingVariants = product.variants.filter(variant => 
          variant.name.toLowerCase().includes(queryLower)
        );
        if (matchingVariants.length > 0) {
          results.push({ ...product, matchType: 'variant', matchingVariants });
        }
      }
    });

    setSearchResults(results);
    setShowGlobalSearch(results.length > 0);
  };

  // Voice search functionality
  const startVoiceSearch = () => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
      const recognition = new SpeechRecognition();

      recognition.continuous = false;
      recognition.interimResults = false;
      recognition.lang = 'en-US';

      recognition.onstart = () => {
        setIsListening(true);
      };

      recognition.onresult = (event) => {
        const transcript = event.results[0][0].transcript;
        setSearchQuery(transcript);
        performGlobalSearch(transcript);
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };

      recognition.onend = () => {
        setIsListening(false);
      };

      recognitionRef.current = recognition;
      recognition.start();
    } else {
      alert('Speech recognition not supported in this browser');
    }
  };

  const stopVoiceSearch = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
    setIsListening(false);
  };

  const navigateToProduct = (product) => {
    setActiveCategory(product.category);
    setActiveSubcategory(product.subcategory);
    setShowGlobalSearch(false);
    setSearchQuery("");
    setSearchResults([]);
  };

  const highlightText = (text, query) => {
    if (!query) return text;
    const regex = new RegExp(`(${query})`, "gi");
    return (
      <span
        dangerouslySetInnerHTML={{
          __html: text.replace(regex, (match) => `<mark class="bg-yellow-200 px-1 rounded">${match}</mark>`),
        }}
      />
    );
  };

  const getStockStatus = (variants) => {
    const totalStock = variants.reduce((sum, v) => sum + v.stock, 0);
    const hasOutOfStock = variants.some(v => v.stock === 0);
    const hasLowStock = variants.some(v => v.stock > 0 && v.stock <= 5);

    if (totalStock === 0) return { status: 'out', color: 'red', text: 'Out of Stock' };
    if (hasOutOfStock || hasLowStock) return { status: 'low', color: 'yellow', text: 'Low Stock' };
    return { status: 'good', color: 'green', text: 'In Stock' };
  };

  const getPriceRange = (variants) => {
    const prices = variants.map(v => v.sellingPrice);
    const min = Math.min(...prices);
    const max = Math.max(...prices);
    return min === max ? `â‚¹${min.toLocaleString()}` : `â‚¹${min.toLocaleString()} - â‚¹${max.toLocaleString()}`;
  };

  const updateQuantity = (productId, variantId, value) => {
    const key = `${productId}-${variantId}`;
    setOrderQuantities(prev => ({
      ...prev,
      [key]: {
        ...prev[key],
        quantity: Math.max(0, value)
      }
    }));
  };

  const getCurrentProducts = () => {
    let products;

    if (viewMode === "categories") {
      products = productData.filter(p => 
        p.category === activeCategory && p.subcategory === activeSubcategory
      );
    } else {
      // Group by distributors
      const distributorProducts = {};
      productData.forEach(product => {
        if (!distributorProducts[product.distributor]) {
          distributorProducts[product.distributor] = [];
        }
        distributorProducts[product.distributor].push(product);
      });
      products = distributorProducts[activeCategory] || [];
    }

    // Apply filters
    return products.filter(product => {
      if (filterType === "low-stock") {
        return product.variants.some(v => v.stock > 0 && v.stock <= 5);
      } else if (filterType === "out-of-stock") {
        return product.variants.some(v => v.stock === 0);
      }
      return true;
    }).sort((a, b) => {
      let aVal, bVal;
      switch (sortBy) {
        case 'stock':
          aVal = a.variants.reduce((sum, v) => sum + v.stock, 0);
          bVal = b.variants.reduce((sum, v) => sum + v.stock, 0);
          break;
        case 'price':
          aVal = Math.min(...a.variants.map(v => v.sellingPrice));
          bVal = Math.min(...b.variants.map(v => v.sellingPrice));
          break;
        default:
          aVal = a.name.toLowerCase();
          bVal = b.name.toLowerCase();
      }

      if (sortOrder === 'desc') {
        return aVal < bVal ? 1 : -1;
      }
      return aVal > bVal ? 1 : -1;
    });
  };

  const currentProducts = getCurrentProducts();

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50">
      {/* Header */}
      <div className="sticky top-0 z-50 bg-white shadow-lg border-b">
        <div className="px-3 sm:px-6 py-4">
          {/* Top Bar */}
          <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4 mb-4">
            <div className="flex items-center space-x-4">
              <h1 className="text-xl sm:text-2xl font-bold text-gray-900">Inventory</h1>
              <span className="text-sm text-gray-500">
                {currentProducts.length} products
              </span>
            </div>

            <div className="flex items-center space-x-2 sm:space-x-4 w-full sm:w-auto">
              {/* Global Search */}
              <div className="relative flex-1 sm:flex-initial sm:w-80">
                <input
                  type="text"
                  placeholder="Search products, distributors, variants..."
                  value={searchQuery}
                  onChange={(e) => {
                    setSearchQuery(e.target.value);
                    performGlobalSearch(e.target.value);
                  }}
                  className="w-full pl-10 pr-16 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                />
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />

                {/* Voice Search Button */}
                <button
                  onClick={isListening ? stopVoiceSearch : startVoiceSearch}
                  className={`absolute right-2 top-1/2 transform -translate-y-1/2 p-1.5 rounded-md transition-colors ${
                    isListening 
                      ? 'bg-red-100 text-red-600 animate-pulse' 
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                >
                  {isListening ? <MicOff className="w-4 h-4" /> : <Mic className="w-4 h-4" />}
                </button>

                {/* Global Search Results */}
                {showGlobalSearch && (
                  <div className="absolute top-full left-0 right-0 mt-2 bg-white border border-gray-200 rounded-lg shadow-lg z-50 max-h-64 overflow-y-auto">
                    {searchResults.map(product => (
                      <div
                        key={`search-${product.id}`}
                        onClick={() => navigateToProduct(product)}
                        className="p-3 hover:bg-gray-50 cursor-pointer border-b border-gray-100 last:border-b-0"
                      >
