    </div>
  )}
  <div className="mt-3 flex items-center justify-between">
    <div className="flex items-center space-x-2">
      <button
        onClick={() => {
          const key = `${product.id}-${variant.id}`;
          const curr = orderQuantities[key]?.quantity || 0;
          updateQuantity(product.id, variant.id, Math.max(0, curr - 1));
        }}
        disabled={variant.stock === 0}
        className="p-1 rounded bg-gray-200 hover:bg-gray-300 disabled:opacity-50 disabled:cursor-not-allowed"
      >
        <Minus className="w-4 h-4" />
      </button>
      <input
        type="number"
        min="0"
        max={variant.stock}
        value={orderQuantities[`${product.id}-${variant.id}`]?.quantity || 0}
        onChange={(e) => updateQuantity(product.id, variant.id, Math.min(variant.stock, Math.max(0, parseInt(e.target.value) || 0)))}
        disabled={variant.stock === 0}
        className="w-16 px-2 py-1 text-center border rounded focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
      />
      <button
        onClick={() => {
          const key = `${product.id}-${variant.id}`;
          const curr = orderQuantities[key]?.quantity || 0;
          updateQuantity(product.id, variant.id, Math.min(variant.stock, curr + 1));
        }}
        disabled={variant.stock === 0}
        className="p-1 rounded bg-gray-200 hover:bg-gray-300 disabled:opacity-50 disabled:cursor-not-allowed"
      >
        <Plus className="w-4 h-4" />
      </button>
    </div>
    <select
      value={orderQuantities[`${product.id}-${variant.id}`]?.unit || "box"}
