import { useState, useEffect } from "react";
import { Search, Filter, Plus, Minus, Mic, ShoppingCart, X, ChevronRight } from "lucide-react";

export default function Shelf() {
  const [hoveredRow, setHoveredRow] = useState(null);
  const [showHiddenColumns, setShowHiddenColumns] = useState(false);
  const [filterType, setFilterType] = useState("all"); // all, low-stock, out-of-stock
  const [searchQuery, setSearchQuery] = useState("");
  const [activeCategory, setActiveCategory] = useState("Electronics");
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [orderQuantities, setOrderQuantities] = useState({});
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [showHeader, setShowHeader] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [showCategoryMenu, setShowCategoryMenu] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      // If scrolled down more than 20px, hide the header
      if (currentScrollY > lastScrollY && currentScrollY > 20) {
        setShowHeader(false);
      } else {
        setShowHeader(true);
      }

      setLastScrollY(currentScrollY);
    };

    window.addEventListener("scroll", handleScroll, { passive: true });

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [lastScrollY]);


  // Initialize order quantities
  useEffect(() => {
    const initialQuantities = {};
    productData.forEach(product => {
      product.variants.forEach(variant => {
        initialQuantities[`${product.id}-${variant.id}`] = {
          quantity: 0,
          unit: "box"
        };
      });
    });
    setOrderQuantities(initialQuantities);
  }, []);

  const categories = [
    "Electronics",
    "Clothing",
    "Home Goods",
    "Groceries",
    "Beauty",
    "Office Supplies"
  ];

  // Sample product data
  const productData = [
    {
      id: 1,
      name: "Smartphone X",
      icon: "ðŸ“±",
      distributor: "TechGlobal",
      category: "Electronics",
      variants: [
        { id: 1, name: "64GB Black", stock: 15, price: 699, expiry: "N/A", sku: "SMX-64-B" },
        { id: 2, name: "128GB Black", stock: 8, price: 799, expiry: "N/A", sku: "SMX-128-B" },
        { id: 3, name: "64GB White", stock: 2, price: 699, expiry: "N/A", sku: "SMX-64-W" },
        { id: 4, name: "128GB White", stock: 0, price: 799, expiry: "N/A", sku: "SMX-128-W" }
      ]
    },
    {
      id: 2,
      name: "Bluetooth Headphones",
      icon: "ðŸŽ§",
      distributor: "AudioTech",
      category: "Electronics",
      variants: [
        { id: 1, name: "Black", stock: 25, price: 129, expiry: "N/A", sku: "BH-BLK" },
        { id: 2, name: "White", stock: 18, price: 129, expiry: "N/A", sku: "BH-WHT" },
        { id: 3, name: "Blue", stock: 5, price: 129, expiry: "N/A", sku: "BH-BLU" }
      ]
    },
    {
      id: 3,
      name: "Cotton T-Shirt",
      icon: "ðŸ‘•",
      distributor: "FashionWear",
      category: "Clothing",
      variants: [
        { id: 1, name: "Small White", stock: 30, price: 19.99, expiry: "N/A", sku: "CT-S-W" },
        { id: 2, name: "Medium White", stock: 25, price: 19.99, expiry: "N/A", sku: "CT-M-W" },
        { id: 3, name: "Large White", stock: 20, price: 19.99, expiry: "N/A", sku: "CT-L-W" },
        { id: 4, name: "Small Black", stock: 30, price: 19.99, expiry: "N/A", sku: "CT-S-B" },
        { id: 5, name: "Medium Black", stock: 20, price: 19.99, expiry: "N/A", sku: "CT-M-B" },
        { id: 6, name: "Large Black", stock: 15, price: 19.99, expiry: "N/A", sku: "CT-L-B" }
      ]
    },
    {
      id: 4,
      name: "Organic Milk",
      icon: "ðŸ¥›",
      distributor: "FarmFresh",
      category: "Groceries",
      variants: [
        { id: 1, name: "1 Liter", stock: 40, price: 3.99, expiry: "2025-05-15", sku: "OM-1L" },
        { id: 2, name: "2 Liter", stock: 25, price: 6.99, expiry: "2025-05-15", sku: "OM-2L" }
      ]
    }
  ];

  const MobileHeader = () => (
    <div className={`fixed top-0 left-0 right-0 z-50 bg-white shadow-md transition-transform duration-300 ${showHeader ? 'translate-y-0' : '-translate-y-full'}`}>
      <div className="flex items-center justify-between p-4">
        <button 
          onClick={() => setShowCategoryMenu(!showCategoryMenu)}
          className="flex items-center gap-2 px-3 py-2 bg-blue-800 text-white rounded-lg"
        >
          <span>{activeCategory}</span>
          <ChevronRight className={`h-4 w-4 transition-transform ${showCategoryMenu ? 'rotate-90' : ''}`} />
        </button>

        <div className="flex items-center gap-2">
          <div className="relative">
            <input
              type="text"
              placeholder="Search..."
              className="w-24 sm:w-40 pl-8 pr-2 py-2 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-blue-500"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyDownCapture={(e) => {
                if (e.key === "Enter") {
                  handleSearch(e.target.value);
                }
              }}
            />
            <Search className="absolute left-2 text-gray-400 h-4 w-4" />
          </div>

          <button
            className="relative p-2 bg-blue-800 text-white rounded-lg"
            onClick={() => setShowCart(!showCart)}
          >
            <ShoppingCart className="h-5 w-5" />
            {cartItems.length > 0 && (
              <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs w-4 h-4 flex items-center justify-center rounded-full">
                {cartItems.length}
              </span>
            )}
          </button>
        </div>
      </div>

      {/* Mobile filter buttons */}
      <div className="flex justify-between px-4 pb-3 overflow-x-auto gap-2">
        <button
          onClick={() => setFilterType("all")}
          className={`px-3 py-1.5 text-xs rounded-lg flex-shrink-0 ${
            filterType === "all"
              ? "bg-blue-800 text-white"
              : "bg-gray-100 text-gray-700 border border-gray-200"
          }`}
        >
          All
        </button>
        <button
          onClick={() => setFilterType("low-stock")}
          className={`px-3 py-1.5 text-xs rounded-lg flex-shrink-0 ${
            filterType === "low-stock"
              ? "bg-yellow-500 text-white"
              : "bg-gray-100 text-gray-700 border border-gray-200"
          }`}
        >
          Low Stock
        </button>
        <button
          onClick={() => setFilterType("out-of-stock")}
          className={`px-3 py-1.5 text-xs rounded-lg flex-shrink-0 ${
            filterType === "out-of-stock"
              ? "bg-red-500 text-white"
              : "bg-gray-100 text-gray-700 border border-gray-200"
          }`}
        >
          Out Stock
        </button>
        <button
          onClick={toggleHiddenColumns}
          className={`px-3 py-1.5 text-xs rounded-lg flex-shrink-0 bg-gray-100 text-gray-700 border border-gray-200 flex items-center gap-1`}
        >
          <Filter className="h-3 w-3" />
          {showHiddenColumns ? "Hide" : "Show"}
        </button>
      </div>

      {/* Mobile category menu dropdown */}
      {showCategoryMenu && (
        <div className="absolute left-0 right-0 bg-white border-t border-b border-gray-200 shadow-lg z-50">
          <div className="py-2">
            {categories.map(category => (
              <button
                key={category}
                onClick={() => {
                  setActiveCategory(category);
                  setShowCategoryMenu(false);
                }}
                className={`w-full text-left px-6 py-3 ${
                  activeCategory === category ? "bg-blue-50 text-blue-800" : ""
                }`}
              >
                {category}
              </button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
  const MobileCart = () => (
    <div className={`fixed inset-0 bg-gray-800 bg-opacity-75 z-50 transition-opacity ${showCart ? 'opacity-100' : 'opacity-0 pointer-events-none'}`}>
      <div className={`absolute inset-x-0 bottom-0 bg-white rounded-t-2xl shadow-xl transition-transform duration-300 ${showCart ? 'translate-y-0' : 'translate-y-full'}`}>
        <div className="flex justify-between items-center p-4 border-b">
          <h3 className="text-lg font-semibold">Shopping Cart ({cartItems.length})</h3>
          <button 
            onClick={() => setShowCart(false)}
            className="p-1 rounded-full hover:bg-gray-100"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        <div className="max-h-96 overflow-y-auto p-4 space-y-3">
          {cartItems.length === 0 ? (
            <p className="text-center text-gray-500 py-8">Your cart is empty</p>
          ) : (
            <>
              {cartItems.map(item => (
                <div key={item.id} className="flex justify-between items-center pb-3 border-b">
                  <div className="flex items-center">
                    <div className="flex-shrink-0 h-10 w-10 flex items-center justify-center bg-blue-50 rounded-lg text-lg mr-3">
                      {item.productIcon}
                    </div>
                    <div>
                      <p className="font-medium">{item.productName}</p>
                      <p className="text-sm text-gray-600">{item.variantName}</p>
                      <p className="text-sm">
                        {item.quantity} {item.unit}(s) x ${item.price}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center">
                    <p className="font-medium mr-3">${item.totalPrice.toFixed(2)}</p>
                    <button 
                      onClick={() => removeFromCart(item.id)}
                      className="text-red-500"
                    >
                      <X className="h-5 w-5" />
                    </button>
                  </div>
                </div>
              ))}

              <div className="mt-6 space-y-3">
                <div className="flex justify-between font-semibold text-lg">
                  <span>Total:</span>
                  <span>${getCartTotal()}</span>
                </div>

                <button className="w-full py-3 bg-blue-800 text-white rounded-lg font-medium">
                  Checkout
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
  const toggleHiddenColumns = () => {
    setShowHiddenColumns(!showHiddenColumns);
  };

  const getPriceRange = (variants) => {
    const prices = variants.map(v => v.price);
    const min = Math.min(...prices);
    const max = Math.max(...prices);
    return min === max ? `$${min}` : `$${min} - $${max}`;
  };

  const getTotalStock = (variants) => {
    return variants.reduce((sum, variant) => sum + variant.stock, 0);
  };

  const getVariantNames = (variants) => {
    return variants.map(v => v.name).join(", ");
  };

  const getExpiryDates = (variants) => {
    const expiryDates = [...new Set(variants.map(v => v.expiry))];
    return expiryDates.join(", ");
  };

  const getSKUs = (variants) => {
    return variants.map(v => v.sku).join(", ");
  };

  const updateQuantity = (productId, variantId, value) => {
    const key = `${productId}-${variantId}`;
    setOrderQuantities(prev => ({
      ...prev,
      [key]: {
        ...prev[key],
        quantity: Math.max(0, value)
      }
    }));
  };

  const updateUnit = (productId, variantId, unit) => {
    const key = `${productId}-${variantId}`;
    setOrderQuantities(prev => ({
      ...prev,
      [key]: {
        ...prev[key],
        unit
      }
    }));
  };

  // Count total items ordered for a product
  const getTotalOrderedItems = (product) => {
    let total = 0;
    product.variants.forEach(variant => {
      const key = `${product.id}-${variant.id}`;
      total += orderQuantities[key]?.quantity || 0;
    });
    return total;
  };

  // Check if product has any low stock or out of stock variants
  const hasLowStock = (product) => {
    return product.variants.some(v => v.stock > 0 && v.stock <= 5);
  };

  const hasOutOfStock = (product) => {
    return product.variants.some(v => v.stock === 0);
  };

  const getOrderedItemsLabel = (product) => {
    const totalOrdered = getTotalOrderedItems(product);
    const hasOut = hasOutOfStock(product);
    const hasLow = hasLowStock(product);

    if (totalOrdered === 0 && !hasOut && !hasLow) {
      return null; // Return null when no orders and stock levels are fine
    }

    let labels = [];

    if (totalOrdered > 0) {
      labels.push(`${totalOrdered} ordered`);
    }

    if (hasOut) {
      labels.push("Out of stock");
    } else if (hasLow) {
      labels.push("Low stock");
    }

    return labels.join(" â€¢ ");
  };

  const addAllToCart = () => {
    // Create cart items for all products with quantities > 0
    const newCartItems = [];

    productData.forEach(product => {
      // Filter variants that have quantity > 0
      const selectedVariants = product.variants.filter(variant => {
        const key = `${product.id}-${variant.id}`;
        return orderQuantities[key]?.quantity > 0;
      });

      if (selectedVariants.length > 0) {
        selectedVariants.forEach(variant => {
          const key = `${product.id}-${variant.id}`;
          newCartItems.push({
            id: `${product.id}-${variant.id}`,
            productId: product.id,
            variantId: variant.id,
            productName: product.name,
            productIcon: product.icon,
            variantName: variant.name,
            price: variant.price,
            quantity: orderQuantities[key].quantity,
            unit: orderQuantities[key].unit,
            totalPrice: variant.price * orderQuantities[key].quantity
          });
        });
      }
    });

    if (newCartItems.length === 0) {
      alert("Please select quantity for at least one product variant");
      return;
    }

    // Add to cart
    setCartItems(prev => [...prev, ...newCartItems]);

    // Reset all quantities
    const updatedQuantities = { ...orderQuantities };
    Object.keys(updatedQuantities).forEach(key => {
      updatedQuantities[key] = {
        ...updatedQuantities[key],
        quantity: 0
      };
    });
    setOrderQuantities(updatedQuantities);

    // Show cart
    setShowCart(true);
  };

  const removeFromCart = (itemId) => {
    setCartItems(prev => prev.filter(item => item.id !== itemId));
  };

  const getCartTotal = () => {
    return cartItems.reduce((sum, item) => sum + item.totalPrice, 0).toFixed(2);
  };

  const startVoiceSearch = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.onresult = (event) => {
      const voiceQuery = event.results[0][0].transcript;
      setSearchQuery(voiceQuery);
      handleSearch(voiceQuery);
    };
    recognition.start();
  };

  const handleSearch = (query) => {
    if (!query) {
      return;
    }

    setSearchQuery(query);

    // Check if there are matching products in any category
    const matchingProducts = productData.filter(product => matchesSearch(product, query));

    // If there are matches and none in current category, switch to the first category with matches
    if (matchingProducts.length > 0) {
      const currentCategoryHasMatches = matchingProducts.some(product => product.category === activeCategory);

      if (!currentCategoryHasMatches) {
        // Switch to the category of the first matching product
        setActiveCategory(matchingProducts[0].category);
      }
    }
  };

  const matchesSearch = (product, query) => {
    if (!query) return true;

    const searchTerms = query.toLowerCase().split(" ");

    // Check if ALL search terms are found somewhere in the product data
    return searchTerms.every(term => {
      // Check product name
      if (product.name.toLowerCase().includes(term)) return true;

      // Check distributor name
      if (product.distributor.toLowerCase().includes(term)) return true;

      // Check variant names
      if (product.variants.some(v => v.name.toLowerCase().includes(term))) return true;

      // Check SKUs
      if (product.variants.some(v => v.sku.toLowerCase().includes(term))) return true;

      return false;
    });
  };

  const filteredProducts = productData
    .filter(product => {
      // Filter by category
      if (product.category !== activeCategory) return false;

      // Filter by search query
      if (!matchesSearch(product, searchQuery)) return false;

      // Filter by stock status
      if (filterType === "low-stock") {
        return product.variants.some(v => v.stock > 0 && v.stock <= 5);
      } else if (filterType === "out-of-stock") {
        return product.variants.some(v => v.stock === 0);
      }

      return true;
    });

    return (
      <div className="min-h-screen relative font-sans bg-gray-50">
        {/* Remove desktop-only fixed elements */}
        {/* Add Mobile Header */}
        <MobileHeader />

        {/* Main Content - with adjusted top margin for mobile header */}
        <div className="container mx-auto px-2 sm:px-4 py-4 mt-24">
          {/* Remove desktop Category Navigation */}

          {/* Mobile-friendly layout */}
          <div className="flex flex-col gap-6">
            {/* Responsive inventory table */}
            <div className="bg-white rounded-xl shadow-lg overflow-x-auto w-full">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-3 sm:px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Product
                    </th>
                    {/* Hide variant column on mobile */}
                    <th className="hidden sm:table-cell px-3 sm:px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Variants
                    </th>
                    <th className="px-3 sm:px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Stock
                    </th>
                    <th className="px-3 sm:px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Rate
                    </th>
                    {/* Always hide these columns on mobile */}
                    {showHiddenColumns && (
                      <>
                        <th className="hidden md:table-cell px-3 sm:px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          Expiry
                        </th>
                        <th className="hidden md:table-cell px-3 sm:px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                          SKU
                        </th>
                      </>
                    )}
                    <th className="px-3 sm:px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {/* Table rows with simpler mobile layout */}
                  {filteredProducts.map((product) => (
                    <tr 
                      key={product.id}
                      className="transition-all duration-200 ease-in-out hover:bg-gray-50"
                      onClick={() => setHoveredRow(hoveredRow === product.id ? null : product.id)}
                    >
                      <td className="px-3 sm:px-6 py-4">
                        <div className="flex items-start">
                          <div className="flex-shrink-0 h-10 w-10 sm:h-12 sm:w-12 flex items-center justify-center bg-blue-50 rounded-xl text-xl sm:text-2xl shadow-sm">
                            {product.icon}
                          </div>
                          <div className="ml-3 sm:ml-4 flex-1">
                            <div className="text-sm font-medium text-gray-900">{product.name}</div>
                            <div className="text-xs text-gray-500">{product.distributor}</div>

                            {/* Expanded order form - visible on click on mobile */}
                            {hoveredRow === product.id && (
                              <div className="mt-3 bg-gray-50 p-3 rounded-xl border shadow-sm">
                                <div className="space-y-2">
                                  {product.variants.map(variant => (
                                    <div key={variant.id} className="flex items-center justify-between flex-wrap gap-2">
                                      <span className="text-xs font-medium">
                                        {variant.name}
                                        {variant.stock === 0 && (
                                          <span className="ml-1 text-red-500 text-xs font-semibold">(Out)</span>
                                        )}
                                        {variant.stock > 0 && variant.stock <= 5 && (
                                          <span className="ml-1 text-yellow-500 text-xs font-semibold">({variant.stock})</span>
                                        )}
                                      </span>
                                      <div className="flex items-center">
                                        <button 
                                          className="bg-gray-200 p-1 rounded-l-lg hover:bg-gray-300 transition-colors"
                                          onClick={(e) => {
                                            e.stopPropagation();
                                            const key = `${product.id}-${variant.id}`;
                                            const currentQty = orderQuantities[key]?.quantity || 0;
                                            updateQuantity(product.id, variant.id, Math.max(0, currentQty - 1));
                                          }}
                                        >
                                          <Minus className="h-3 w-3 sm:h-4 sm:w-4" />
                                        </button>
                                        <input
                                          type="number"
                                          className="w-10 px-1 py-1 border-t border-b text-center text-xs"
                                          min="0"
                                          value={orderQuantities[`${product.id}-${variant.id}`]?.quantity || 0}
                                          onChange={(e) => {
                                            e.stopPropagation();
                                            updateQuantity(product.id, variant.id, parseInt(e.target.value) || 0);
                                          }}
                                          onClick={(e) => e.stopPropagation()}
                                        />
                                        <button 
                                          className="bg-gray-200 p-1 rounded-r-lg hover:bg-gray-300 transition-colors"
                                          onClick={(e) => {
                                            e.stopPropagation();
                                            const key = `${product.id}-${variant.id}`;
                                            const currentQty = orderQuantities[key]?.quantity || 0;
                                            updateQuantity(product.id, variant.id, currentQty + 1);
                                          }}
                                        >
                                          <Plus className="h-3 w-3 sm:h-4 sm:w-4" />
                                        </button>
                                        <select 
                                          className="ml-2 border rounded-lg px-2 py-1 text-xs bg-white"
                                          value={orderQuantities[`${product.id}-${variant.id}`]?.unit || "box"}
                                          onChange={(e) => {
                                            e.stopPropagation();
                                            updateUnit(product.id, variant.id, e.target.value);
                                          }}
                                          onClick={(e) => e.stopPropagation()}
                                        >
                                          <option value="box">Box</option>
                                          <option value="piece">Piece</option>
                                          <option value="pack">Pack</option>
                                        </select>
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}
                          </div>
                        </div>
                      </td>
                      <td className="hidden sm:table-cell px-3 sm:px-6 py-4">
                        <div className="text-xs text-gray-500 truncate max-w-24 sm:max-w-32">{getVariantNames(product.variants)}</div>
                      </td>
                      <td className="px-3 sm:px-6 py-4">
                        <div className={`text-sm font-medium ${
                          getTotalStock(product.variants) === 0 
                            ? 'text-red-500' 
                            : getTotalStock(product.variants) <= 5 
                              ? 'text-yellow-500' 
                              : 'text-green-500'
                        }`}>
                          {getTotalStock(product.variants)}
                        </div>
                      </td>
                      <td className="px-3 sm:px-6 py-4">
                        <div className="text-sm text-gray-900">{getPriceRange(product.variants)}</div>
                      </td>
                      {showHiddenColumns && (
                        <>
                          <td className="hidden md:table-cell px-3 sm:px-6 py-4">
                            <div className="text-sm text-gray-900">{getExpiryDates(product.variants)}</div>
                          </td>
                          <td className="hidden md:table-cell px-3 sm:px-6 py-4">
                            <div className="text-sm text-gray-500">{getSKUs(product.variants)}</div>
                          </td>
                        </>
                      )}
                      <td className="px-3 sm:px-6 py-4">
                        {getOrderedItemsLabel(product) ? (
                          <div className="flex flex-wrap gap-1 items-center">
                            {/* More compact badges for mobile */}
                            {getTotalOrderedItems(product) > 0 && (
                              <span className="inline-flex items-center px-1.5 sm:px-2 py-0.5 sm:py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                {getTotalOrderedItems(product)}
                              </span>
                            )}

                            {hasOutOfStock(product) && (
                              <span className="inline-flex items-center px-1.5 sm:px-2 py-0.5 sm:py-1 rounded-full text-xs font-medium bg-red-100 text-red-800">
                                Out
                              </span>
                            )}

                            {!hasOutOfStock(product) && hasLowStock(product) && (
                              <span className="inline-flex items-center px-1.5 sm:px-2 py-0.5 sm:py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                                Low
                              </span>
                            )}
                          </div>
                        ) : (
                          <div>&nbsp;</div>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {/* Add to Cart button - Fixed at the bottom for mobile */}
        <div className="fixed bottom-4 left-0 w-full px-4 z-30">
          <button
            onClick={addAllToCart}
            className="w-full shadow-lg py-3 bg-blue-800 text-white rounded-xl flex items-center justify-center gap-2 font-medium"
          >
            <ShoppingCart className="h-5 w-5" />
            Add Selected to Cart
          </button>
        </div>

        {/* Mobile Cart */}
        <MobileCart />
      </div>
    )};
