import React, { useState, useEffect } from 'react';
import { ChevronDown, ChevronUp, Plus, Search, Filter, RefreshCw, Package, Truck, Download } from 'lucide-react';
import ProductTable from './ProductTable'; // Your existing component

const InventoryPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filters, setFilters] = useState({
    category: 'all',
    stockStatus: 'all',
    distributor: 'all'
  });
  const [orderItems, setOrderItems] = useState([]);
  const [showOrderPanel, setShowOrderPanel] = useState(false);

  useEffect(() => {
    // Fetch products from backend
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/products'); // Replace with your API endpoint
      if (!response.ok) {
        throw new Error('Failed to fetch products');
      }
      const data = await response.json();
      setProducts(data);
      setError(null);
    } catch (err) {
      setError(err.message);
      console.error('Error fetching products:', err);
    } finally {
      setLoading(false);
    }
  };

  const addToOrder = (variant, quantity) => {
    const existingItemIndex = orderItems.findIndex(item => item.variantId === variant.id);

    if (existingItemIndex > -1) {
      // Update existing item
      const updatedItems = [...orderItems];
      updatedItems[existingItemIndex].quantity += quantity;
      setOrderItems(updatedItems);
    } else {
      // Add new item
      setOrderItems([...orderItems, {
        variantId: variant.id,
        productName: variant.productName || '',
        variantName: variant.variantValue,
        distributor: variant.distributor,
        unitPrice: variant.unitPrice,
        quantity: quantity
      }]);
    }

    // Show order panel when adding items
    setShowOrderPanel(true);
  };

  const removeOrderItem = (index) => {
    const newItems = [...orderItems];
    newItems.splice(index, 1);
    setOrderItems(newItems);
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleFilterChange = (filterType, value) => {
    setFilters({
      ...filters,
      [filterType]: value
    });
  };

  const filteredProducts = products.filter(product => {
    // Search filter
    const matchesSearch = 
      searchQuery === '' ||
      product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.variants.some(v => 
        v.variantValue.toLowerCase().includes(searchQuery.toLowerCase()) ||
        v.sku?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        v.distributor.toLowerCase().includes(searchQuery.toLowerCase())
      );

    // Category filter
    const matchesCategory = 
      filters.category === 'all' || 
      product.category === filters.category;

    // Stock status filter
    let matchesStockStatus = true;
    if (filters.stockStatus !== 'all') {
      if (filters.stockStatus === 'out') {
        matchesStockStatus = product.variants.some(v => v.stock === 0);
      } else if (filters.stockStatus === 'low') {
        matchesStockStatus = product.variants.some(v => v.stock > 0 && v.stock <= v.reorderPoint);
      } else if (filters.stockStatus === 'good') {
        matchesStockStatus = product.variants.every(v => v.stock > v.reorderPoint);
      }
    }

    // Distributor filter
    const matchesDistributor = 
      filters.distributor === 'all' || 
      product.variants.some(v => v.distributor === filters.distributor);

    return matchesSearch && matchesCategory && matchesStockStatus && matchesDistributor;
  });

  // Extract unique categories and distributors for filter dropdowns
  const categories = ['all', ...new Set(products.map(p => p.category))];
  const distributors = ['all', ...new Set(products.flatMap(p => p.variants.map(v => v.distributor)))];

  // Group products by category
  const productsByCategory = filteredProducts.reduce((acc, product) => {
    const category = product.category || 'Uncategorized';
    if (!acc[category]) {
      acc[category] = [];
    }
    acc[category].push(product);
    return acc;
  }, {});

  const createOrder = async () => {
    if (orderItems.length === 0) return;

    try {
      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          items: orderItems,
          date: new Date().toISOString(),
          status: 'pending'
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to create order');
      }

      // Order created successfully
      setOrderItems([]);
      setShowOrderPanel(false);
      // Show success notification or redirect
    } catch (err) {
      console.error('Error creating order:', err);
      // Show error notification
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <RefreshCw className="animate-spin text-blue-500" size={32} />
        <span className="ml-2">Loading inventory...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 text-red-700 p-4 rounded-lg">
        <p>Error loading inventory: {error}</p>
        <button 
          onClick={fetchProducts}
          className="mt-2 px-4 py-2 bg-red-100 text-red-700 rounded-md hover:bg-red-200"
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="p-4 max-w-full">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-800 mb-4 md:mb-0">Inventory Management</h1>

        <div className="flex flex-wrap gap-2">
          <button 
            onClick={() => setShowOrderPanel(!showOrderPanel)}
            className={`px-4 py-2 rounded-md ${orderItems.length > 0 
              ? 'bg-blue-600 text-white' 
              : 'bg-gray-200 text-gray-800'}`}
          >
            <Truck size={16} className="inline mr-2" />
            {orderItems.length > 0 ? `Order (${orderItems.length})` : 'Create Order'}
          </button>

          <button className="px-4 py-2 bg-gray-200 text-gray-800 rounded-md">
            <Download size={16} className="inline mr-2" />
            Export
          </button>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow mb-6 p-4">
        <div className="flex flex-col md:flex-row gap-4">
          <div className="flex-1 relative">
            <Search size={18} className="absolute left-3 top-3 text-gray-400" />
            <input
              type="text"
              placeholder="Search products, SKU, variant..."
              value={searchQuery}
              onChange={handleSearch}
              className="w-full px-10 py-2 border border-gray-300 rounded-md"
            />
          </div>

          <div className="flex flex-wrap gap-2">
            <div className="relative">
              <select
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
                className="appearance-none px-4 py-2 pr-8 border border-gray-300 rounded-md"
              >
                <option value="all">All Categories</option>
                {categories.filter(c => c !== 'all').map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
              <ChevronDown size={16} className="absolute right-2 top-3 text-gray-400 pointer-events-none" />
            </div>

            <div className="relative">
              <select
                value={filters.stockStatus}
                onChange={(e) => handleFilterChange('stockStatus', e.target.value)}
                className="appearance-none px-4 py-2 pr-8 border border-gray-300 rounded-md"
              >
                <option value="all">All Stock Status</option>
                <option value="out">Out of Stock</option>
                <option value="low">Low Stock</option>
                <option value="good">Good Stock</option>
              </select>
              <ChevronDown size={16} className="absolute right-2 top-3 text-gray-400 pointer-events-none" />
            </div>

            <div className="relative">
              <select
                value={filters.distributor}
                onChange={(e) => handleFilterChange('distributor', e.target.value)}
                className="appearance-none px-4 py-2 pr-8 border border-gray-300 rounded-md"
              >
                <option value="all">All Distributors</option>
                {distributors.filter(d => d !== 'all').map(distributor => (
                  <option key={distributor} value={distributor}>{distributor}</option>
                ))}
              </select>
              <ChevronDown size={16} className="absolute right-2 top-3 text-gray-400 pointer-events-none" />
            </div>

            <button 
              onClick={() => {
                setSearchQuery('');
                setFilters({
                  category: 'all',
                  stockStatus: 'all',
                  distributor: 'all'
                });
              }}
              className="px-4 py-2 text-sm text-gray-600 bg-gray-100 rounded-md"
            >
              Clear Filters
            </button>
          </div>
        </div>
      </div>

      {/* Stats Summary */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-gray-500 text-sm font-medium">Total Products</h3>
          <div className="flex items-center mt-2">
            <Package size={20} className="text-blue-500" />
            <span className="text-2xl font-bold ml-2">{products.length}</span>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-gray-500 text-sm font-medium">Low Stock Items</h3>
          <div className="flex items-center mt-2">
            <span className="w-5 h-5 rounded-full bg-yellow-400"></span>
            <span className="text-2xl font-bold ml-2">
              {products.filter(p => 
                p.variants.some(v => v.stock > 0 && v.stock <= v.reorderPoint)
              ).length}
            </span>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-gray-500 text-sm font-medium">Out of Stock</h3>
          <div className="flex items-center mt-2">
            <span className="w-5 h-5 rounded-full bg-red-500"></span>
            <span className="text-2xl font-bold ml-2">
              {products.filter(p => p.variants.some(v => v.stock === 0)).length}
            </span>
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow">
          <h3 className="text-gray-500 text-sm font-medium">Total Variants</h3>
          <div className="flex items-center mt-2">
            <Filter size={20} className="text-purple-500" />
            <span className="text-2xl font-bold ml-2">
              {products.reduce((total, product) => total + product.variants.length, 0)}
            </span>
          </div>
        </div>
      </div>

      {/* Order Panel */}
      {showOrderPanel && (
        <div className="bg-white rounded-lg shadow mb-6 p-4">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-semibold">Order Items ({orderItems.length})</h2>
            <button 
              onClick={() => setShowOrderPanel(false)}
              className="text-gray-500 hover:text-gray-700"
            >
              <ChevronUp size={20} />
            </button>
          </div>

          {orderItems.length > 0 ? (
            <>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead>
                    <tr className="bg-gray-50">
                      <th className="px-4 py-2 text-left text-xs font-semibold text-gray-500">Product</th>
                      <th className="px-4 py-2 text-left text-xs font-semibold text-gray-500">Variant</th>
                      <th className="px-4 py-2 text-left text-xs font-semibold text-gray-500">Distributor</th>
                      <th className="px-4 py-2 text-left text-xs font-semibold text-gray-500">Unit Price</th>
                      <th className="px-4 py-2 text-left text-xs font-semibold text-gray-500">Quantity</th>
                      <th className="px-4 py-2 text-left text-xs font-semibold text-gray-500">Total</th>
                      <th className="px-4 py-2 text-left text-xs font-semibold text-gray-500">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orderItems.map((item, index) => (
                      <tr key={index} className="border-b">
                        <td className="px-4 py-2">{item.productName}</td>
                        <td className="px-4 py-2">{item.variantName}</td>
                        <td className="px-4 py-2">{item.distributor}</td>
                        <td className="px-4 py-2">${item.unitPrice}</td>
                        <td className="px-4 py-2">
                          <input
                            type="number"
                            min="1"
                            value={item.quantity}
                            onChange={(e) => {
                              const newItems = [...orderItems];
                              newItems[index].quantity = parseInt(e.target.value, 10) || 1;
                              setOrderItems(newItems);
                            }}
                            className="w-16 px-2 py-1 border rounded-md"
                          />
                        </td>
                        <td className="px-4 py-2">${(item.unitPrice * item.quantity).toFixed(2)}</td>
                        <td className="px-4 py-2">
                          <button
                            onClick={() => removeOrderItem(index)}
                            className="text-red-500 hover:text-red-700"
                          >
                            Remove
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                  <tfoot>
                    <tr className="font-semibold">
                      <td colSpan="5" className="px-4 py-2 text-right">Total:</td>
                      <td className="px-4 py-2">
                        ${orderItems.reduce((total, item) => total + (item.unitPrice * item.quantity), 0).toFixed(2)}
                      </td>
                      <td></td>
                    </tr>
                  </tfoot>
                </table>
              </div>
              <div className="mt-4 flex justify-end">
                <button
                  onClick={createOrder}
                  className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                >
                  Create Purchase Order
                </button>
              </div>
            </>
          ) : (
            <div className="text-center py-10 text-gray-500">
              <Package size={48} className="mx-auto mb-4 text-gray-300" />
              <p>No items added to order yet</p>
            </div>
          )}
        </div>
      )}

      {/* Inventory Tables by Category */}
      {Object.keys(productsByCategory).length > 0 ? (
        Object.entries(productsByCategory).map(([category, categoryProducts]) => (
          <div key={category} className="mb-6">
            <h2 className="text-xl font-semibold mb-3">{category}</h2>
            <ProductTable
              products={categoryProducts}
              addToOrder={addToOrder}
              initialShowAll={false}
              defaultUnit="box"
            />
          </div>
        ))
      ) : (
        <div className="bg-white p-10 rounded-lg shadow text-center">
          <Package size={64} className="mx-auto mb-4 text-gray-300" />
          <h3 className="text-xl font-medium text-gray-700 mb-2">No products found</h3>
          <p className="text-gray-500">
            {searchQuery || filters.category !== 'all' || filters.stockStatus !== 'all' || filters.distributor !== 'all'
              ? 'Try adjusting your search or filters'
              : 'Start by adding products to your inventory'}
          </p>
        </div>
      )}
    </div>
  );
};

export default InventoryPage;
