const QuantityEditor = ({ value, onChange }) => {
    const handleChange = (e) => {
      const val = e.target.value;
      // Allow empty string to support continuous typing
      if (val === "" || /^[0-9]+$/.test(val)) {
        onChange(val === "" ? 0 : parseInt(val));
      }
    };

    const handleDropdownChange = (e) => {
      const adjustment = parseInt(e.target.value);
      const newVal = value + adjustment;
      if (!isNaN(newVal)) onChange(newVal);
    };

    return (
      <div className="flex items-center gap-1">
        <button
          className="bg-gray-200 px-2 py-1 rounded text-lg"
          onClick={() => onChange(value - 1)}
        >
          -
        </button>
        <input
          type="number"
          value={value}
          onChange={handleChange}
          className="w-16 text-center border rounded px-2 py-1"
        />
        <button
          className="bg-gray-200 px-2 py-1 rounded text-lg"
          onClick={() => onChange(value + 1)}
        >
          +
        </button>
        <select
          onChange={handleDropdownChange}
          defaultValue=""
          className="ml-2 border rounded px-1 py-1"
        >
          <option value="" disabled>
            Â±5
          </option>
          {[...Array(5)].map((_, i) => (
            <React.Fragment key={i}>
              <option value={i + 1}>+{i + 1}</option>
              <option value={-1 * (i + 1)}>-{i + 1}</option>
            </React.Fragment>
          ))}
        </select>
      </div>
    );
  };
