import { NavLink, Outlet } from "react-router-dom";
import { ShoppingCart, Package, CreditCard, User, List } from "lucide-react";

export default function Layout() {
  const navItems = [
    { name: "Shop", to: "/layout/shop", icon: List },
    { name: "Shelf", to: "/layout/shelf", icon: Package },
    { name: "Payment", to: "/layout/payment", icon: CreditCard },
    { name: "Orders", to: "/layout/orders", icon: ShoppingCart },
    { name: "You", to: "/layout/you", icon: User }
  ]
  return (
    <div className="min-h-screen pb-20">
      {/* Render the current page inside Layout */}
      <Outlet />

      {/* Bottom Navigation */}
      <div className="rounded-sm fixed bottom-0 left-0 w-full bg-gray-100 text-black flex justify-around px-4 py-3 shadow-lg border-t-4">
        {navItems.map(({ name, to, icon: Icon }) => (
          <NavLink
            key={to}
            to={to}
            className={({ isActive }) =>
              `flex flex-col items-center text-sm transition duration-300 ${
                isActive ? "text-blue-600 border-t-4 border-indigo-500" : "text-gray-600 hover:text-blue-500"
              }`
            }
          >
            <Icon className="w-5 h-5 mb-1" />
            {name}
          </NavLink>
        ))}
      </div>
    </div>
  );
}
