import { useState } from "react";
import { Lock, User, ShieldCheck } from "lucide-react";

export default function LoginPage() {
  const [email, setEmail] = useState(""); 
  const [password, setPassword] = useState(""); 
  const [otp, setOtp] = useState(""); 
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Flag to control whether we are in test mode or real login mode
  const isTestMode = true; // Set to `true` for testing, `false` for real login

  const handleLogin = async () => {
    if (!email || !password) {
      setError("Please fill in all fields");
      return;
    }

    setLoading(true);
    setError("");

    try {
      if (isTestMode) {
        // Skip the actual login process for testing
        console.log('Test mode - bypassing login.');
        // Simulate API delay
        await new Promise(resolve => setTimeout(resolve, 1000));

        // Store user info in component state instead of localStorage
        console.log('Login successful - redirecting...');
        alert('Login successful! (Test Mode)');
        return;
      }

      // Real login flow would go here
      // const { data } = await API.post('/retailers/login', {
      //   email,
      //   password,
      // });

      // For now, simulate successful login
      setStep(2); // Move to OTP step

    } catch (error) {
      setError(error.response?.data?.message || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  const verifyOtp = async () => {
    if (!otp) {
      setError("Please enter OTP");
      return;
    }

    setLoading(true);
    setError("");

    try {
      // Simulate OTP verification
      await new Promise(resolve => setTimeout(resolve, 1000));
      console.log("OTP verified successfully");
      alert('OTP verified successfully!');

      // Reset form after successful verification
      setEmail("");
      setPassword("");
      setOtp("");
      setStep(1);
    } catch (error) {
      setError('OTP verification failed');
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e, action) => {
    if (e.key === 'Enter') {
      action();
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="p-8 rounded-lg w-full max-w-md bg-white shadow-xl">
        <h2 className="text-3xl font-extrabold text-center text-gray-900">Login</h2>
        <p className="text-center text-gray-600 mt-2">Access your account securely</p>

        {error && (
          <div className="mt-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded-md">
            {error}
          </div>
        )}

        {step === 1 ? (
          <div className="mt-6 space-y-4">
            <div className="flex items-center border rounded-md px-3 py-2 bg-gray-50 focus-within:border-blue-500">
              <User className="w-5 h-5 text-gray-500 mr-2" />
              <input
                type="email"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                onKeyPress={(e) => handleKeyPress(e, handleLogin)}
                className="flex-1 bg-transparent focus:outline-none"
                disabled={loading}
              />
            </div>
            <div className="flex items-center border rounded-md px-3 py-2 bg-gray-50 focus-within:border-blue-500">
              <Lock className="w-5 h-5 text-gray-500 mr-2" />
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                onKeyPress={(e) => handleKeyPress(e, handleLogin)}
                className="flex-1 bg-transparent focus:outline-none"
                disabled={loading}
              />
            </div>
            <button 
              onClick={handleLogin}
              disabled={loading}
              className="w-full bg-gradient-to-r from-blue-700 to-indigo-900 text-white py-3 rounded-md shadow-md text-lg font-semibold hover:scale-105 transition-transform disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:scale-100"
            >
              {loading ? 'Logging in...' : 'Next'}
            </button>
          </div>
        ) : (
          <div className="mt-6 space-y-4">
            <p className="text-center text-gray-600">Enter the OTP sent to your email</p>
            <div className="flex items-center border rounded-md px-3 py-2 bg-gray-50 focus-within:border-blue-500">
              <ShieldCheck className="w-5 h-5 text-gray-500 mr-2" />
              <input
                type="text"
                placeholder="Enter OTP"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                onKeyPress={(e) => handleKeyPress(e, verifyOtp)}
                className="flex-1 bg-transparent focus:outline-none"
                maxLength={6}
                disabled={loading}
              />
            </div>
            <button 
              onClick={verifyOtp} 
              disabled={loading}
              className="w-full bg-green-600 text-white py-3 rounded-md shadow-md text-lg font-semibold hover:scale-105 transition-transform disabled:opacity-50 disabled:cursor-not-allowed disabled:hover:scale-100"
            >
              {loading ? 'Verifying...' : 'Verify OTP'}
            </button>
            <button 
              onClick={() => setStep(1)}
              className="w-full bg-gray-500 text-white py-2 rounded-md text-sm hover:bg-gray-600 transition-colors"
            >
              Back to Login
            </button>
          </div>
        )}

        <p className="text-center text-gray-600 mt-4">
          Don't have an account?  
          <button className="text-blue-600 hover:underline ml-1">
            Sign up
          </button>
        </p>
      </div>
    </div>
  );
}
