import React, { useState } from "react";
import { Trash, Check, FileText, ArrowDown, ArrowUp } from "lucide-react";

const dummyOrders = [
  {
    shopName: "Shop A",
    orderId: "ORD123",
    items: [
      { id: 1, item: "Product A", brand: "Brand X", quantity: 2, rate: 100 },
      { id: 2, item: "Product B", brand: "Brand Y", quantity: 1, rate: 150 },
      { id: 3, item: "Product C", brand: "Brand Z", quantity: 3, rate: 80 },
      { id: 4, item: "Product D", brand: "Brand X", quantity: 2, rate: 90 },
    ],
  },
  {
    shopName: "Shop B",
    orderId: "ORD456",
    items: [
      { id: 5, item: "Product E", brand: "Brand X", quantity: 1, rate: 120 },
      { id: 6, item: "Product F", brand: "Brand Y", quantity: 2, rate: 70 },
      { id: 7, item: "Product G", brand: "Brand Z", quantity: 3, rate: 60 },
      { id: 8, item: "Product H", brand: "Brand X", quantity: 1, rate: 200 },
    ],
  },
];

const ShopDashboard = () => {
  const [upcomingOrders, setUpcomingOrders] = useState(dummyOrders);
  const [paymentOrders, setPaymentOrders] = useState([]);
  const [expandedCards, setExpandedCards] = useState({});
  const [showReports, setShowReports] = useState(false);
  const [completedOrders, setCompletedOrders] = useState([]);

  const toggleExpand = (shopName) => {
    setExpandedCards((prev) => ({ ...prev, [shopName]: !prev[shopName] }));
  };

  const moveToPayment = (orderId) => {
    const order = upcomingOrders.find((o) => o.orderId === orderId);
    setPaymentOrders((prev) => [...prev, order]);
    setUpcomingOrders((prev) => prev.filter((o) => o.orderId !== orderId));
  };

  const deleteOrderCard = (orderId) => {
    if (window.confirm("Are you sure to delete this order?")) {
      setUpcomingOrders((prev) => prev.filter((o) => o.orderId !== orderId));
    }
  };

  const deleteItem = (orderId, itemId) => {
    setUpcomingOrders((prev) =>
      prev.map((o) =>
        o.orderId === orderId
          ? { ...o, items: o.items.filter((item) => item.id !== itemId) }
          : o
      )
    );
  };

  const handlePayment = (orderId, mode) => {
    const now = new Date();
    const order = paymentOrders.find((o) => o.orderId === orderId);
    const bill = {
      ...order,
      mode,
      date: now.toLocaleDateString(),
      time: now.toLocaleTimeString(),
    };
    console.log("Generated Bill:", bill);
    if (mode === "credit") {
      alert("Credit bill generated. Redirect to borrower's page...");
    } else {
      alert("Paid bill generated.");
    }
    setPaymentOrders((prev) => prev.filter((o) => o.orderId !== orderId));
    setCompletedOrders((prev) => [...prev, bill]);
  };

  const calculateTotal = (items) =>
    items.reduce((acc, item) => acc + item.quantity * item.rate, 0);

  return (
    <div className="p-6 space-y-8 bg-white min-h-screen">
      <h1 className="text-3xl font-bold">Shop Dashboard</h1>

      {/* --- Upcoming Orders --- */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Upcoming Orders</h2>
        {upcomingOrders.length === 0 ? (
          <p className="text-gray-500">No upcoming orders.</p>
        ) : (
          upcomingOrders.map((order) => {
            const showAll = expandedCards[order.shopName];
            const itemsToShow = showAll ? order.items : order.items.slice(0, 3);
            return (
              <div
                key={order.orderId}
                className="border rounded-xl shadow p-4 mb-4 bg-gray-50"
              >
                <div className="flex justify-between items-center mb-2">
                  <h3 className="text-xl font-bold">
                    {order.shopName} - #{order.orderId}
                  </h3>
                  <button
                    className="text-red-600 hover:text-red-800"
                    onClick={() => deleteOrderCard(order.orderId)}
                  >
                    <Trash />
                  </button>
                </div>
                <ul className="space-y-2">
                  {itemsToShow.map((item) => (
                    <li
                      key={item.id}
                      className="flex justify-between border-b pb-2"
                    >
                      <div>
                        <p className="font-medium">{item.item}</p>
                        <p className="text-sm text-gray-600">
                          Brand: {item.brand}, Qty: {item.quantity}, Rate: ₹{item.rate}
                        </p>
                      </div>
                      <button
                        onClick={() => deleteItem(order.orderId, item.id)}
                        className="text-red-500 hover:text-red-700"
                      >
                        <Trash size={16} />
                      </button>
                    </li>
                  ))}
                </ul>
                {order.items.length > 3 && (
                  <button
                    className="text-blue-600 text-sm mt-2"
                    onClick={() => toggleExpand(order.shopName)}
                  >
                    {showAll ? "View Less" : "View More"} {showAll ? <ArrowUp size={12} /> : <ArrowDown size={12} />}
                  </button>
                )}
                <div className="flex justify-between items-center mt-4">
                  <p className="font-semibold">
                    Total: ₹{calculateTotal(order.items)}
                  </p>
                  <button
                    onClick={() => moveToPayment(order.orderId)}
                    className="bg-green-600 text-white px-4 py-2 rounded-full hover:bg-green-700"
                  >
                    <Check className="inline-block mr-2" /> Move to Payment
                  </button>
                </div>
              </div>
            );
          })
        )}
      </section>

      {/* --- Payment Section --- */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Payment Section</h2>
        {paymentOrders.length === 0 ? (
          <p className="text-gray-500">No orders for payment.</p>
        ) : (
          paymentOrders.map((order) => (
            <div
              key={order.orderId}
              className="border rounded-xl shadow p-4 mb-4 bg-green-50"
            >
              <h3 className="text-xl font-bold mb-2">
                {order.shopName} - #{order.orderId}
              </h3>
              <ul className="space-y-1">
                {order.items.map((item) => (
                  <li key={item.id} className="text-sm text-gray-700">
                    {item.item} - Qty: {item.quantity} | ₹{item.rate} | Brand: {item.brand}
                  </li>
                ))}
              </ul>
              <div className="flex justify-between items-center mt-4">
                <p className="font-semibold">
                  Total: ₹{calculateTotal(order.items)}
                </p>
                <div className="space-x-2">
                  <button
                    onClick={() => handlePayment(order.orderId, "cash")}
                    className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
                  >
                    Cash
                  </button>
                  <button
                    onClick={() => handlePayment(order.orderId, "upi")}
                    className="bg-purple-500 text-white px-3 py-1 rounded hover:bg-purple-600"
                  >
                    UPI
                  </button>
                  <button
                    onClick={() => handlePayment(order.orderId, "credit")}
                    className="bg-orange-500 text-white px-3 py-1 rounded hover:bg-orange-600"
                  >
                    Credit
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </section>

      {/* --- Reports Section --- */}
      <section>
        <button
          onClick={() => setShowReports(!showReports)}
          className="bg-black text-white px-4 py-2 rounded-full hover:bg-gray-800"
        >
          <FileText className="inline-block mr-2" /> Reports
        </button>
        {showReports && (
          <div className="mt-4 border p-4 rounded-xl bg-gray-100">
            <h3 className="text-lg font-semibold mb-2">Business Summary</h3>
            <p>Total Revenue Today: ₹2,500</p>
            <p>Profit Today: ₹400</p>
            <p>Monthly Revenue: ₹55,000</p>
            <p>Monthly Profit: ₹8,200</p>
            <div className="mt-4">
              <h4 className="font-semibold mb-2">Completed Orders</h4>
              {completedOrders.length === 0 ? (
                <p className="text-gray-500">No completed orders.</p>
              ) : (
                <ul className="text-sm text-gray-700 space-y-2">
                  {completedOrders.map((order) => (
                    <li key={order.orderId}>
                      <strong>{order.shopName}</strong> - #{order.orderId} - ₹{calculateTotal(order.items)} - {order.mode.toUpperCase()} on {order.date} at {order.time}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        )}
      </section>
    </div>
  );
};

export default ShopDashboard;
