import { useState, useEffect } from "react";
import { Search, Filter, Plus, Minus, Mic, ShoppingCart } from "lucide-react";
import API from "../api";
import { FixedSizeList as List } from "react-window";
import AutoSizer from "react-virtualized-auto-sizer";

export default function Shelf() {
  // States for header, filters, search and products
  const [hoveredRow, setHoveredRow] = useState(null);
  const [showHiddenColumns, setShowHiddenColumns] = useState(false);
  const [filterType, setFilterType] = useState("all"); // "all", "low-stock", "out-of-stock"
  const [searchQuery, setSearchQuery] = useState("");
  const [activeCategory, setActiveCategory] = useState("Electronics");
  const [cartItems, setCartItems] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [orderQuantities, setOrderQuantities] = useState({});
  const [showHeader, setShowHeader] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);
  const [productData, setProductData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Categories list
  const categories = [
    "Electronics",
    "Clothing",
    "Home Goods",
    "Groceries",
    "Beauty",
    "Office Supplies"
  ];

  // Handle scrolling to toggle header visibility
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      setShowHeader(currentScrollY < lastScrollY || currentScrollY < 20);
      setLastScrollY(currentScrollY);
    };
    fetchProducts();
    window.addEventListener("scroll", handleScroll, { passive: true });
    return () => window.removeEventListener("scroll", handleScroll);
  }, [lastScrollY]);

  // Fetch products and initialize order quantities
  const fetchProducts = async () => {
    try {
      setIsLoading(true);
      setError(null);
      const response = await API.get(`/products`);
      // Normalize response data
      const data = response && response.data && Array.isArray(response.data)
        ? response.data
        : [];

      if (data.length === 0) console.log("No products found");
      setProductData(data);
      // Initialize quantities
      const initQty = {};
      data.forEach(product => {
        if (product?.variants && Array.isArray(product.variants)) {
          product.variants.forEach(variant => {
            if (product.id && variant.id)
              initQty[`${product.id}-${variant.id}`] = {
                quantity: 0,
                unit: "box"
              };
          });
        }
      });
      setOrderQuantities(initQty);
    } catch (err) {
      console.error("Error fetching products:", err);
      setError(err.message || "Failed to fetch products");
    } finally {
      setIsLoading(false);
    }
  };

  // Utility functions
  const highlightText = (text, query) => {
    if (!query) return text;
    const regex = new RegExp(`(${query})`, "gi");
    return (
      <span
        dangerouslySetInnerHTML={{
          __html: text.replace(regex, (match) => `<mark class="bg-yellow-200">${match}</mark>`)
        }}
      />
    );
  };

  const getSellingPriceRange = (variants) => {
    const prices = variants.map(v => v.sellingPrice);
    const min = Math.min(...prices);
    const max = Math.max(...prices);
    return min === max ? `₹${min}` : `₹${min} - ₹${max}`;
  };
  const getCostPriceRange = (variants) => {
    const prices = variants.map(v => v.costPrice);
    const min = Math.min(...prices);
    const max = Math.max(...prices);
    return min === max ? `₹${min}` : `₹${min} - ₹${max}`;
  };
  const getTotalStock = (variants) =>
    variants.reduce((sum, v) => sum + v.stock, 0);
  const getVariantNames = (variants) =>
    // Instead of join (which fails on React elements) use map with a separator
    variants.map((v, i) => (
      <span key={v.id}>
        {i > 0 && ", "}
        {highlightText(v.name, searchQuery)}
      </span>
    ));
  const getExpiryDates = (variants) => {
    const dates = [...new Set(variants.map(v => v.expiry))];
    return dates.join(", ");
  };
  const getSKUs = (variants) => variants.map(v => v.sku).join(", ");

  const updateQuantity = (productId, variantId, value) => {
    const key = `${productId}-${variantId}`;
    setOrderQuantities(prev => ({
      ...prev,
      [key]: { ...prev[key], quantity: Math.max(0, value) }
    }));
  };
  const updateUnit = (productId, variantId, unit) => {
    const key = `${productId}-${variantId}`;
    setOrderQuantities(prev => ({
      ...prev,
      [key]: { ...prev[key], unit }
    }));
  };
  const getTotalOrderedItems = (product) => {
    let total = 0;
    product.variants.forEach(variant => {
      const key = `${product.id}-${variant.id}`;
      total += orderQuantities[key]?.quantity || 0;
    });
    return total;
  };

  const hasLowStock = (product) =>
    product.variants.some(v => v.stock > 0 && v.stock <= 5);
  const hasOutOfStock = (product) =>
    product.variants.some(v => v.stock === 0);
  const getOrderedItemsLabel = (product) => {
    const totalOrdered = getTotalOrderedItems(product);
    let labels = [];
    if (totalOrdered > 0) labels.push(`${totalOrdered} ordered`);
    if (hasOutOfStock(product)) labels.push("Out of stock");
    else if (hasLowStock(product)) labels.push("Low stock");
    return labels.length ? labels.join(" • ") : null;
  };

  // Voice search
  const startVoiceSearch = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.onresult = (event) => {
      const voiceQuery = event.results[0][0].transcript;
      setSearchQuery(voiceQuery);
      handleSearch(voiceQuery);
    };
    recognition.start();
  };

  const handleSearch = (query) => {
    if (!query) return;
    setSearchQuery(query);
    const matches = productData.filter(product => matchesSearch(product, query));
    if (matches.length > 0 && !matches.some(p => p.category === activeCategory)) {
      setActiveCategory(matches[0].category);
    }
  };

  const matchesSearch = (product, query) => {
    if (!query) return true;
    const terms = query.toLowerCase().split(" ");
    return terms.every(term => {
      return (
        product.name.toLowerCase().includes(term) ||
        product.distributor.toLowerCase().includes(term) ||
        product.variants.some(v => v.name.toLowerCase().includes(term)) ||
        product.variants.some(v => v.sku.toLowerCase().includes(term))
      );
    });
  };

  // Virtualized Row component for each product card
  const Row = ({ index, style }) => {
    const product = filteredProducts[index];
    if (!product) return null;
    return (
      <div style={style} className="p-2">
        <div className="bg-white rounded-xl shadow-lg p-4 border border-gray-100 transition hover:shadow-2xl">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-shrink-0 h-16 w-16 flex items-center justify-center bg-blue-100 rounded-full text-2xl">
              {product.icon}
            </div>
            <div className="flex-1">
              <h3 className="text-lg font-bold text-blue-900">
                {highlightText(product.name, searchQuery)}
              </h3>
              <p className="text-sm text-gray-500">{product.distributor}</p>
              <p className="text-xs text-gray-400">{getVariantNames(product.variants)}</p>
              <div className="flex flex-wrap gap-2 mt-2">
                <span className={`text-xs font-semibold ${getTotalStock(product.variants) === 0 ? "text-red-500" : getTotalStock(product.variants) <= 5 ? "text-yellow-500" : "text-green-600"}`}>
                  Stock: {getTotalStock(product.variants)}
                </span>
                <span className="text-xs text-gray-700">
                  Price: {getSellingPriceRange(product.variants)}
                </span>
                {showHiddenColumns && (
                  <>
                    <span className="text-xs text-gray-500">
                      Cost: {getCostPriceRange(product.variants)}
                    </span>
                    <span className="text-xs text-gray-500">
                      Expiry: {getExpiryDates(product.variants)}
                    </span>
                    <span className="text-xs text-gray-500">
                      SKU: {getSKUs(product.variants)}
                    </span>
                  </>
                )}
              </div>
            </div>
            <div className="flex flex-col items-end">
              {getOrderedItemsLabel(product) && (
                <span className="px-2 py-1 text-xs rounded-full bg-blue-100 text-blue-800">
                  {getOrderedItemsLabel(product)}
                </span>
              )}
            </div>
          </div>
          {/* Expanded order form on hover */}
          {hoveredRow === product.id && (
            <div className="mt-4 p-4 bg-blue-50 rounded-lg border border-blue-100 animate-fade-in">
              {product.variants.map(variant => (
                <div key={variant.id} className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium">
                    {highlightText(variant.name, searchQuery)}
                    {variant.stock === 0 && (
                      <span className="ml-1 text-red-500 text-xs">(Out of stock)</span>
                    )}
                    {variant.stock > 0 && variant.stock <= 5 && (
                      <span className="ml-1 text-yellow-500 text-xs">(Low: {variant.stock})</span>
                    )}
                  </span>
                  <div className="flex items-center">
                    <button
                      className="px-2 py-1 bg-gray-200 rounded-l hover:bg-gray-300 transition"
                      onClick={() => {
                        const key = `${product.id}-${variant.id}`;
                        const curr = orderQuantities[key]?.quantity || 0;
                        updateQuantity(product.id, variant.id, curr - 1);
                      }}
                    >
                      <Minus className="h-4 w-4" />
                    </button>
                    <input
                      type="number"
                      className="w-12 text-center border-t border-b"
                      min="0"
                      value={orderQuantities[`${product.id}-${variant.id}`]?.quantity || 0}
                      onChange={(e) =>
                        updateQuantity(product.id, variant.id, parseInt(e.target.value) || 0)
                      }
                    />
                    <button
                      className="px-2 py-1 bg-gray-200 rounded-r hover:bg-gray-300 transition"
                      onClick={() => {
                        const key = `${product.id}-${variant.id}`;
                        const curr = orderQuantities[key]?.quantity || 0;
                        updateQuantity(product.id, variant.id, curr + 1);
                      }}
                    >
                      <Plus className="h-4 w-4" />
                    </button>
                    <select
                      className="ml-2 border rounded px-2 py-1 text-xs"
                      value={orderQuantities[`${product.id}-${variant.id}`]?.unit || "box"}
                      onChange={(e) =>
                        updateUnit(product.id, variant.id, e.target.value)
                      }
                    >
                      <option value="box">Box</option>
                      <option value="piece">Piece</option>
                      <option value="pack">Pack</option>
                    </select>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  };

  // Filter products by category, search query and stock status
  const filteredProducts = productData.filter(product => {
    if (product.category !== activeCategory) return false;
    if (!matchesSearch(product, searchQuery)) return false;
    if (filterType === "low-stock")
      return product.variants.some(v => v.stock > 0 && v.stock <= 5);
    if (filterType === "out-of-stock")
      return product.variants.some(v => v.stock === 0);
    return true;
  });

  // Cart logic
  const addAllToCart = () => {
    const newItems = [];
    productData.forEach(product => {
      product.variants.forEach(variant => {
        const key = `${product.id}-${variant.id}`;
        const qty = orderQuantities[key]?.quantity || 0;
        if (qty > 0) {
          newItems.push({
            id: key,
            productId: product.id,
            variantId: variant.id,
            productName: product.name,
            productIcon: product.icon,
            variantName: variant.name,
            price: variant.sellingPrice,
            quantity: qty,
            unit: orderQuantities[key]?.unit || "box",
            totalPrice: variant.sellingPrice * qty
          });
        }
      });
    });
    if (!newItems.length) {
      alert("Please select quantity for at least one product variant.");
      return;
    }
    setCartItems(prev => [...prev, ...newItems]);
    resetOrderQuantities();
    setShowCart(true);
  };

  const resetOrderQuantities = () => {
    const newQuantities = { ...orderQuantities };
    Object.keys(newQuantities).forEach(key => {
      newQuantities[key] = { ...newQuantities[key], quantity: 0 };
    });
    setOrderQuantities(newQuantities);
  };

  const getCartTotal = () =>
    cartItems.reduce((sum, item) => sum + item.totalPrice, 0).toFixed(2);

  const removeFromCart = (id) =>
    setCartItems(prev => prev.filter(item => item.id !== id));

  const handleCheckout = async () => {
    try {
      const orderData = {
        items: cartItems.map(i => ({
          productId: i.productId,
          variantId: i.variantId,
          quantity: i.quantity,
          unit: i.unit
        })),
        totalPrice: parseFloat(getCartTotal())
      };
      const response = await API.post(`/orders`);
      if (!response.ok) throw new Error(`HTTP error: ${response.status}`);
      const result = await response.json();
      alert(`Order #${result.orderId} submitted successfully!`);
      setCartItems([]);
      setShowCart(false);
      fetchProducts();
    } catch (err) {
      alert(`Failed to submit order: ${err.message}`);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 via-white to-white font-sans">
      {/* Sticky header with filters and search */}
      <header className={`sticky top-0 z-50 transition-all duration-300 ${showHeader ? "block" : "hidden"}`}>
        <div className="bg-gradient-to-b from-blue-900 to-blue-700 shadow-lg py-4 px-6">
          <div className="flex flex-wrap items-center justify-between gap-4">
            <div className="flex gap-2">
              <button
                onClick={() => setFilterType("all")}
                className={`px-4 py-2 rounded-xl font-semibold transition ${
                  filterType === "all" ? "bg-blue-800 text-white" : "bg-gray-100 text-gray-700"
                }`}
              >
                All Items
              </button>
              <button
                onClick={() => setFilterType("low-stock")}
                className={`px-4 py-2 rounded-xl font-semibold transition ${
                  filterType === "low-stock" ? "bg-yellow-500 text-white" : "bg-gray-100 text-gray-700"
                }`}
              >
                Low Stock
              </button>
              <button
                onClick={() => setFilterType("out-of-stock")}
                className={`px-4 py-2 rounded-xl font-semibold transition ${
                  filterType === "out-of-stock" ? "bg-red-500 text-white" : "bg-gray-100 text-gray-700"
                }`}
              >
                Out of Stock
              </button>
              <button
                onClick={() => setShowHiddenColumns(!showHiddenColumns)}
                className="px-4 py-2 rounded-xl bg-gray-100 text-gray-700 flex items-center gap-1 transition"
              >
                <Filter className="h-4 w-4" />
                {showHiddenColumns ? "Hide" : "Show"} SKU/Expiry/Cost
              </button>
            </div>
            {/* Category Navigation */}
            <div className="flex gap-2 overflow-x-auto">
              {categories.map(cat => (
                <button
                  key={cat}
                  onClick={() => setActiveCategory(cat)}
                  className={`px-5 py-2 rounded-xl whitespace-nowrap font-semibold transition ${
                    activeCategory === cat ? "bg-white text-blue-900 shadow-md border" : "bg-blue-100 text-blue-800 border"
                  }`}
                >
                  {cat}
                </button>
              ))}
            </div>
            {/* Search & Voice */}
            <div className="relative">
              <input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={e => setSearchQuery(e.target.value)}
                onKeyDownCapture={e => {
                  if (e.key === "Enter") handleSearch(e.target.value);
                }}
                className="w-64 pl-10 pr-12 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <Search className="absolute left-3 top-2 text-gray-400 h-5 w-5" />
              <button
                onClick={startVoiceSearch}
                className="absolute right-2 top-2 bg-blue-600 text-white px-2 py-1 rounded hover:bg-blue-500 transition"
              >
                <Mic className="h-4 w-4" />
              </button>
            </div>
            {/* Cart Button */}
            <button
              onClick={() => setShowCart(!showCart)}
              className="relative flex items-center gap-2 bg-blue-800 text-white px-4 py-2 rounded-full transition hover:bg-blue-700"
            >
              <ShoppingCart className="h-5 w-5" />
              <span>Cart</span>
              {cartItems.length > 0 && (
                <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs w-6 h-6 flex items-center justify-center rounded-full">
                  {cartItems.length}
                </span>
              )}
            </button>
          </div>
        </div>
      </header>
      {/* Main content: Virtualized List */}
      <main className="container mx-auto px-4 py-8">
        {isLoading ? (
          <p className="text-center text-gray-600">Loading products...</p>
        ) : error ? (
          <p className="text-center text-red-600">{error}</p>
        ) : (
          <div className="bg-white rounded-xl shadow-lg overflow-hidden">
            <AutoSizer disableHeight>
              {({ width }) => (
                <List
                  height={600}
                  itemCount={filteredProducts.length}
                  itemSize={200}
                  width={width}
                >
                  {Row}
                </List>
              )}
            </AutoSizer>
          </div>
        )}
        {/* Floating Add to Cart Button */}
        <div className="fixed bottom-10 right-10 z-50">
          <button
            onClick={addAllToCart}
            className="bg-gradient-to-r from-blue-900 to-blue-700 text-white px-8 py-4 rounded-full shadow-xl transition hover:scale-105 hover:from-blue-800 hover:to-blue-600 text-lg font-bold"
          >
            Add to Cart
          </button>
        </div>
      </main>
      {/* Optionally: Insert your Cart Drawer/Modal here */}
    </div>
  );
}
