import React, { useState } from "react";

const OrderPage = () => {
  const [activeTab, setActiveTab] = useState("pending");
  const [pendingOrders, setPendingOrders] = useState([
    { id: 1, item: "Product A", quantity: 2, rate: 100, checked: false },
    { id: 2, item: "Product B", quantity: 1, rate: 200, checked: false },
  ]);
  const [readyOrders, setReadyOrders] = useState([]);
  const [onWayOrders, setOnWayOrders] = useState([]);
  const [completedOrders, setCompletedOrders] = useState([]);

  const handleCheckboxChange = (id) => {
    setPendingOrders((prev) =>
      prev.map((order) =>
        order.id === id ? { ...order, checked: !order.checked } : order
      )
    );
  };

  const handleSelectAll = (checked) => {
    setPendingOrders((prev) =>
      prev.map((order) => ({ ...order, checked }))
    );
  };

  const handleQuantityChange = (id, quantity) => {
    setPendingOrders((prev) =>
      prev.map((order) =>
        order.id === id ? { ...order, quantity: parseInt(quantity) || 0 } : order
      )
    );
  };

  const calculatePrice = (quantity, rate) => quantity * rate;

  const selectedOrders = pendingOrders.filter((order) => order.checked);
  const finalPrice = selectedOrders.reduce(
    (sum, order) => sum + calculatePrice(order.quantity, order.rate),
    0
  );

  const proceedOrders = () => {
    const toMove = pendingOrders.filter((order) => order.checked);
    setReadyOrders((prev) => [...prev, ...toMove.map(o => ({ ...o, checked: false }))]);
    setPendingOrders((prev) => prev.filter((order) => !order.checked));
  };

  const TabButton = ({ tabId, label }) => (
    <button
      onClick={() => setActiveTab(tabId)}
      className={`px-4 py-2 font-medium rounded-full transition ${
        activeTab === tabId
          ? "bg-blue-600 text-white"
          : "bg-gray-200 text-gray-800 hover:bg-gray-300"
      }`}
    >
      {label}
    </button>
  );

  return (
    <div className="bg-white min-h-screen p-6">
      <h1 className="text-3xl font-bold mb-6">Orders Dashboard</h1>

      {/* Tabs */}
      <div className="flex gap-4 mb-6">
        <TabButton tabId="pending" label="Pending Orders" />
        <TabButton tabId="ready" label="Ready to Order" />
        <TabButton tabId="onWay" label="Orders on Way" />
      </div>

      {/* Tab Content */}
      {activeTab === "pending" && (
        <div>
          <h2 className="text-2xl font-semibold mb-4">🕒 Pending Orders</h2>
          <div className="overflow-auto">
            <table className="w-full text-left border-collapse">
              <thead>
                <tr className="bg-gray-100">
                  <th className="p-3">
                    <input
                      type="checkbox"
                      checked={pendingOrders.every((o) => o.checked)}
                      onChange={(e) => handleSelectAll(e.target.checked)}
                    />
                  </th>
                  <th className="p-3">Item</th>
                  <th className="p-3">Rate</th>
                  <th className="p-3">Quantity</th>
                  <th className="p-3">Price</th>
                </tr>
              </thead>
              <tbody>
                {pendingOrders.map((order) => (
                  <tr key={order.id} className="border-t">
                    <td className="p-3">
                      <input
                        type="checkbox"
                        checked={order.checked}
                        onChange={() => handleCheckboxChange(order.id)}
                      />
                    </td>
                    <td className="p-3">{order.item}</td>
                    <td className="p-3">₹{order.rate}</td>
                    <td className="p-3">
                      <input
                        type="number"
                        value={order.quantity}
                        min="1"
                        onChange={(e) =>
                          handleQuantityChange(order.id, e.target.value)
                        }
                        className="w-16 border rounded px-2 py-1"
                      />
                    </td>
                    <td className="p-3">₹{calculatePrice(order.quantity, order.rate)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="mt-4 flex justify-between items-center">
            <p className="text-xl font-semibold">
              Final Price: ₹{finalPrice}
            </p>
            <button
              onClick={proceedOrders}
              className="bg-blue-600 text-white px-6 py-2 rounded-full hover:bg-blue-700"
            >
              Proceed
            </button>
          </div>
        </div>
      )}

      {activeTab === "ready" && (
        <div>
          <h2 className="text-2xl font-semibold mb-2">✅ Ready to Order</h2>
          <ul className="space-y-2 mt-4">
            {readyOrders.map((order) => (
              <li
                key={order.id}
                className="p-4 bg-white rounded-xl shadow border"
              >
                <p className="font-semibold">{order.item}</p>
                <p className="text-sm text-gray-600">
                  Quantity: {order.quantity} | Rate: ₹{order.rate} | Price: ₹
                  {order.quantity * order.rate}
                </p>
              </li>
            ))}
          </ul>
        </div>
      )}

      {activeTab === "onWay" && (
        <div>
          <h2 className="text-2xl font-semibold mb-2">🚚 Orders on Way</h2>
          <ul className="space-y-2 mt-4">
            {onWayOrders.map((order) => (
              <li
                key={order.id}
                className="p-4 bg-white rounded-xl shadow border"
              >
                <p className="font-semibold">{order.item}</p>
                <p className="text-sm text-gray-600">
                  Quantity: {order.quantity} | Rate: ₹{order.rate}
                </p>
              </li>
            ))}
          </ul>

          <h3 className="text-xl font-semibold mt-6">📦 Completed Orders</h3>
          <ul className="space-y-2 mt-4">
            {completedOrders.map((order) => (
              <li
                key={order.id}
                className="p-4 bg-white rounded-xl shadow border"
              >
                <p className="font-semibold">{order.item}</p>
                <p className="text-sm text-gray-600">
                  Quantity: {order.quantity} | Rate: ₹{order.rate}
                </p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default OrderPage;
