import { useEffect, useRef, useState } from "react";
import { Trash, Check, CircleCheck, CreditCard, Phone } from "lucide-react";

export default function You() {
  const favoriteDistributors = [
    { name: "Hindustan Distributors", contact: "+91 9876543210" },
    { name: "Bharat Traders", contact: "+91 9123456789" },
    { name: "Agro India Supplies", contact: "+91 9988776655" },
    { name: "Dairy Fresh Distributors", contact: "+91 8877665544" },
    { name: "Global Wholesale", contact: "+91 7766554433" },
  ];

  const carouselItems = [
    {
      title: "Slege Delivery Solutions",
      description: "Forget the hectic: from loading to unloading: All Covered.",
      image: "https://via.placeholder.com/300x150/3B82F6/FFFFFF?text=Delivery+Solutions",
    },
    {
      title: "Slege Billing Solutions",
      description: "1 Click and Done: No tangling in bills.",
      image: "https://via.placeholder.com/300x150/10B981/FFFFFF?text=Billing+Solutions",
    },
    {
      title: "Slege Business IOT Solutions",
      description: "We build, you enjoy.",
      image: "https://via.placeholder.com/300x150/8B5CF6/FFFFFF?text=IOT+Solutions",
    },
    {
      title: "Slege Black ALL-IN-ONE",
      description: "Everything everywhere all at once.",
      image: "https://via.placeholder.com/300x150/EF4444/FFFFFF?text=ALL-IN-ONE",
    },
  ];

  // Carousel auto-scroll logic
  const carouselRef = useRef(null);
  const [scrollPos, setScrollPos] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      if (carouselRef.current) {
        const container = carouselRef.current;
        const cardWidth = container.offsetWidth / 2;
        const maxScroll = container.scrollWidth - container.offsetWidth;

        const newPos = scrollPos + cardWidth;
        container.scrollTo({
          left: newPos >= maxScroll ? 0 : newPos,
          behavior: "smooth",
        });
        setScrollPos(newPos >= maxScroll ? 0 : newPos);
      }
    }, 3500);

    return () => clearInterval(interval);
  }, [scrollPos]);

  return (
    <div className="min-h-screen bg-blue-100 p-3 sm:p-4 lg:p-6">
      <div className="flex flex-col lg:flex-row gap-4 lg:gap-6 max-w-7xl mx-auto">
        {/* Your Account Section */}
        <div className="flex-1 bg-blue-600 text-white rounded-lg shadow-lg p-4 sm:p-6">
          <h1 className="text-2xl sm:text-3xl font-bold mb-4 sm:mb-6">Your Account</h1>

          {/* Account Areas */}
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4 mb-6 sm:mb-10">
            <div className="flex items-center bg-blue-700 p-3 sm:p-4 rounded-lg shadow">
              <div className="w-12 h-12 sm:w-14 sm:h-14 bg-green-100 rounded-full flex items-center justify-center flex-shrink-0">
                <CircleCheck className="w-6 h-6 sm:w-8 sm:h-8 text-green-600" />
              </div>
              <div className="ml-3 sm:ml-4 min-w-0">
                <h3 className="text-base sm:text-lg font-bold">Your Orders History</h3>
                <p className="text-xs sm:text-sm text-gray-200">View and manage your past orders.</p>
              </div>
            </div>

            <div className="flex items-center bg-blue-700 p-3 sm:p-4 rounded-lg shadow">
              <div className="w-12 h-12 sm:w-14 sm:h-14 bg-yellow-100 rounded-full flex items-center justify-center flex-shrink-0">
                <Check className="w-6 h-6 sm:w-8 sm:h-8 text-yellow-600" />
              </div>
              <div className="ml-3 sm:ml-4 min-w-0">
                <h3 className="text-base sm:text-lg font-bold">Login and Security</h3>
                <p className="text-xs sm:text-sm text-gray-200">Update your password and account settings.</p>
              </div>
            </div>

            <div className="flex items-center bg-blue-700 p-3 sm:p-4 rounded-lg shadow">
              <div className="w-12 h-12 sm:w-14 sm:h-14 bg-red-100 rounded-full flex items-center justify-center flex-shrink-0">
                <Trash className="w-6 h-6 sm:w-8 sm:h-8 text-red-600" />
              </div>
              <div className="ml-3 sm:ml-4 min-w-0">
                <h3 className="text-base sm:text-lg font-bold">Your Addresses</h3>
                <p className="text-xs sm:text-sm text-gray-200">Manage your saved delivery addresses.</p>
              </div>
            </div>

            <div className="flex items-center bg-blue-700 p-3 sm:p-4 rounded-lg shadow">
              <div className="w-12 h-12 sm:w-14 sm:h-14 bg-purple-100 rounded-full flex items-center justify-center flex-shrink-0">
                <CreditCard className="w-6 h-6 sm:w-8 sm:h-8 text-purple-600" />
              </div>
              <div className="ml-3 sm:ml-4 min-w-0">
                <h3 className="text-base sm:text-lg font-bold">Your Cards and Payments</h3>
                <p className="text-xs sm:text-sm text-gray-200">Manage your saved cards and payment methods.</p>
              </div>
            </div>

            <div className="flex items-center bg-blue-700 p-3 sm:p-4 rounded-lg shadow sm:col-span-2">
