import { NavLink, Outlet } from "react-router-dom";
import { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { PlaneLanding, Library, BanknoteArrowUp, Landmark, User, ScanBarcode } from "lucide-react";
import logo from "../assets/navBarLogo1.png"; // Import your logo

function TogglingPaymentIcon() {
  const [showFirst, setShowFirst] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setShowFirst((prev) => !prev);
    }, 3000); // Change every 3 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="transition-opacity duration-500 ease-in-out">
      {showFirst ? (
        <BanknoteArrowUp className="w-5 h-5 mb-1" />
      ) : (
        <Landmark className="w-5 h-5 mb-1" />
      )}
    </div>
  );
}

export default function Layout() {
  const navItems = [
    { name: "Shop", to: "/layout/shop", icon: ScanBarcode },
    { name: "Shelf", to: "/layout/shelf", icon: Library },
    { name: "Payments", to: "/layout/payment", icon: TogglingPaymentIcon },
    { name: "Orders", to: "/layout/orders", icon: PlaneLanding },
    { name: "You", to: "/layout/you", icon: User },
  ];

  const navigate = useNavigate();
  const location = useLocation();
  const [activeItemPosition, setActiveItemPosition] = useState({ left: 0, width: 0 });
  const [showMobileLogout, setShowMobileLogout] = useState(false);

  useEffect(() => {
    // Small delay to ensure DOM is ready
    const timer = setTimeout(() => {
      const activeIndex = navItems.findIndex(item => location.pathname === item.to);
      if (activeIndex !== -1) {
        const navElement = document.querySelector(`[data-nav-index="${activeIndex}"]`);
        const navbarElement = document.querySelector('.fixed.top-0');

        if (navElement && navbarElement) {
          const rect = navElement.getBoundingClientRect();
          const navbarRect = navbarElement.getBoundingClientRect();
          setActiveItemPosition({
            left: rect.left - navbarRect.left,
            width: rect.width
          });
        }
      }
    }, 50);

    return () => clearTimeout(timer);
  }, [location.pathname]);

  const handleLogout = () => {
    // Clear user session or token (if stored in localStorage or cookies)
    localStorage.removeItem("userInfo");
    // Redirect to the home page
    navigate("/");
  };

  return (
    <div className="min-h-screen">
      {/* Top Navigation Bar */}
      <div className="fixed top-0 left-0 w-full bg-white shadow-md z-50 relative">
        <div className="p-2 md:p-4">
          <div className="flex justify-between items-center nav-container">
            {/* Left Section: Logo and Navigation Items */}
            <div className="flex items-center space-x-2 md:space-x-6 w-full md:w-auto">
              {/* Logo - Hidden on mobile */}
              <img
                src={logo}
                alt="Logo"
                className="hidden md:block h-12 w-auto cursor-pointer"
                onClick={() => navigate("/")}
              />

              {/* Navigation Items */}
              <div className="flex items-center justify-between md:justify-start space-x-1 md:space-x-6 w-full md:w-auto overflow-x-auto scrollbar-hide">
                {navItems.map(({ name, to, icon: Icon }, index) => (
                  <button
                    key={index}
                    data-nav-index={index}
                    onClick={() => {
                      if (name === "You" && window.innerWidth < 768) {
                        // On mobile, toggle logout menu for "You" tab
                        setShowMobileLogout(!showMobileLogout);
                      } else {
                        navigate(to);
                        setShowMobileLogout(false);
                      }
                    }}
                    className={`relative flex flex-col md:flex-row items-center justify-center min-w-0 flex-shrink-0 text-blue-1000 hover:text-blue-700 text-xs md:text-lg font-bold transition px-2 md:px-4 py-2 rounded-md ${
                      location.pathname === to ? "bg-blue-800 text-white" : ""
                    }`}
                  >
                    {/* Show icon on mobile, hide on desktop */}
                    <div className="block md:hidden mb-1">
                      {typeof Icon === 'function' ? <Icon /> : <Icon className="w-4 h-4" />}
                    </div>
                    <span className="text-center">{name}</span>

                    {/* Small dot indicator for "You" tab on mobile to show logout option */}
                    {name === "You" && (
                      <div className="md:hidden absolute -top-1 -right-1 w-2 h-2 bg-red-500 rounded-full animate-pulse"></div>
                    )}
                  </button>
                ))}
              </div>
            </div>

            {/* Right Section: Logout Button - Desktop only */}
            <div className="hidden md:block">
              <button
                onClick={handleLogout}
                className="bg-blue-600 text-white px-6 py-3 rounded-md shadow-md hover:bg-blue-700 transition"
              >
                Logout
              </button>
            </div>
          </div>
        </div>

        {/* Mobile Logout Dropdown */}
        {showMobileLogout && (
          <div className="md:hidden absolute top-full left-0 w-full bg-white shadow-lg border-t z-40">
            <button
              onClick={handleLogout}
              className="w-full text-left px-4 py-3 text-red-600 hover:bg-red-50 font-medium"
            >
              Logout
            </button>
          </div>
        )}

        {/* Bottom border line with gap for active item */}
        <div className="absolute bottom-0 left-0 w-full h-0.5 bg-blue-600">
          {/* Gap for active menu item */}
          <div 
            className="absolute top-0 h-0.5 bg-white transition-all duration-300 ease-in-out"
            style={{
              left: `${activeItemPosition.left}px`,
              width: `${activeItemPosition.width}px`
            }}
          />
        </div>
      </div>

      {/* Content Area */}
      <div>
        <Outlet />
      </div>
    </div>
  );
}
