import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Lock, User, ShieldCheck } from "lucide-react";
import bcrypt from "bcryptjs";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(1);

  const handleLogin = async () => {
    const hashedPassword = await bcrypt.hash(password, 10);
    console.log("Logging in with", email, hashedPassword);
    setStep(2); // Move to OTP verification step
  };

  const verifyOtp = () => {
    console.log("Verifying OTP", otp);
    alert("Login successful");
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <Card className="w-full max-w-md p-8 shadow-lg">
        <CardContent>
          <h2 className="text-3xl font-extrabold text-center text-gray-900">Login</h2>
          <p className="text-center text-gray-600 mt-2">Access your account securely</p>
          {step === 1 ? (
            <div className="mt-6 space-y-4">
              <div className="flex items-center border rounded-md px-3 py-2 bg-white">
                <User className="w-5 h-5 text-gray-500 mr-2" />
                <Input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="flex-1 border-none focus:ring-0"
                />
              </div>
              <div className="flex items-center border rounded-md px-3 py-2 bg-white">
                <Lock className="w-5 h-5 text-gray-500 mr-2" />
                <Input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="flex-1 border-none focus:ring-0"
                />
              </div>
              <Button onClick={handleLogin} className="w-full bg-gradient-to-r from-blue-700 to-indigo-900 text-white py-4 rounded-lg shadow-lg text-lg font-semibold hover:scale-105 transition-transform">
                Next
              </Button>
            </div>
          ) : (
            <div className="mt-6 space-y-4">
              <p className="text-center text-gray-600">Enter the OTP sent to your email</p>
              <div className="flex items-center border rounded-md px-3 py-2 bg-white">
                <ShieldCheck className="w-5 h-5 text-gray-500 mr-2" />
                <Input
                  type="text"
                  placeholder="Enter OTP"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  className="flex-1 border-none focus:ring-0"
                />
              </div>
              <Button onClick={verifyOtp} className="w-full bg-green-600 text-white py-4 rounded-lg shadow-lg text-lg font-semibold hover:scale-105 transition-transform">
                Verify OTP
              </Button>
            </div>
          )}
          <p className="text-center text-gray-600 mt-4">
            Don't have an account? <a href="#" className="text-blue-600 hover:underline">Sign up</a>
          </p>
        </CardContent>
      </Card>
    </div>
  );
}
